const { exec } = require("child_process");
const util = require("util");
const execPromise = util.promisify(exec);

module.exports = {
        config: {
                name: "shell",
                aliases: ["sh", "cmd", "exec"],
                version: "1.0",
                author: "NeoKEX",
                countDown: 5,
                role: 4,
                description: {
                        vi: "Th·ª±c thi l·ªánh shell",
                        en: "Execute shell commands"
                },
                category: "owner",
                guide: {
                        vi: '   {pn} <command>: Th·ª±c thi l·ªánh shell'
                                + '\n   V√≠ d·ª•: {pn} ls -la'
                                + '\n   {pn} node -v',
                        en: '   {pn} <command>: Execute shell command'
                                + '\n   Example: {pn} ls -la'
                                + '\n   {pn} node -v'
                }
        },

        langs: {
                vi: {
                        missingCommand: "‚ö† | Vui l√≤ng nh·∫≠p l·ªánh shell c·∫ßn th·ª±c thi",
                        executing: "‚öô | ƒêang th·ª±c thi l·ªánh...",
                        output: "‚úì | K·∫øt qu·∫£:\n\n%1",
                        error: "‚úó | L·ªói:\n\n%1",
                        timeout: "‚ö† | L·ªánh th·ª±c thi qu√° l√¢u (timeout 30s)"
                },
                en: {
                        missingCommand: "‚ö† | Please enter shell command to execute",
                        executing: "‚öô | Executing command...",
                        output: "‚úì | Output:\n\n%1",
                        error: "‚úó | Error:\n\n%1",
                        timeout: "‚ö† | Command execution timeout (30s)"
                }
        },

        onStart: async function ({ message, args, event, getLang, api }) {
                const command = args.join(" ");
                if (!command)
                        return message.reply(getLang("missingCommand"));

                await message.reply(getLang("executing"));

                try {
                        const { stdout, stderr } = await execPromise(command, {
                                timeout: 30000,
                                maxBuffer: 1024 * 1024 * 10
                        });

                        let output = "";
                        if (stdout) output += stdout;
                        if (stderr) output += stderr;

                        if (!output) output = "Command executed successfully (no output)";

                        if (output.length > 2000) {
                                output = output.substring(0, 1997) + "...";
                        }

                        return message.reply(getLang("output", output));
                } catch (error) {
                        let errorMsg = error.message;
                        if (errorMsg.includes("ETIMEDOUT") || errorMsg.includes("timeout"))
                                return message.reply(getLang("timeout"));

                        if (errorMsg.length > 2000) {
                                errorMsg = errorMsg.substring(0, 1997) + "...";
                        }

                        return message.reply(getLang("error", errorMsg));
                }
        }
};

scripts/cmds/edit.js.content:
const axios = require('axios');
const fs = require('fs-extra'); 
const path = require('path');

const API_ENDPOINT = "https://tawsif.is-a.dev/gemini/nano-banana"; 

function extractImageUrl(message, args, event) {
    let imageUrl = args.find(arg => arg.startsWith('http'));

    if (!imageUrl && event.messageReply && event.messageReply.attachments && event.messageReply.attachments.length > 0) {
        const imageAttachment = event.messageReply.attachments.find(att => att.type === 'photo' || att.type === 'image');
        if (imageAttachment && imageAttachment.url) {
            imageUrl = imageAttachment.url;
        }
    }
    return imageUrl;
}

function extractEditPrompt(rawArgs, imageUrl) {
    let prompt = rawArgs.join(" ");

    if (imageUrl) {
        prompt = prompt.replace(imageUrl, '').trim();
    }

    if (prompt.includes('|')) {
        prompt = prompt.split('|')[0].trim();
    }

    return prompt || "enhance quality";
}


module.exports = {
  config: {
    name: "edit",
    aliases: ["imgedit", "nanoedit"],
    version: "2.3",
    author: "NeoKEX",
    countDown: 15,
    role: 0,
    longDescription: "Edit or modify an existing image using a text prompt.",
    category: "ai-image",
    guide: {
      en: "{pn} [modification prompt] OR reply to an image with [modification prompt]"
    }
  },

  onStart: async function({ message, args, event }) {

    const imageUrl = extractImageUrl(message, args, event);
    const editPrompt = extractEditPrompt(args, imageUrl);

    if (!imageUrl) {
      return message.reply("‚ùå Please provide an image URL or reply to an image to edit.");
    }
    if (!editPrompt) {
        return message.reply("‚ùå Please provide a prompt describing the modification you want to make.");
    }

    message.reaction("‚è≥", event.messageID);
    let tempFilePath; 

    try {
      const fullApiUrl = `${API_ENDPOINT}?prompt=${encodeURIComponent(editPrompt)}&url=${encodeURIComponent(imageUrl)}`;

      const apiResponse = await axios.get(fullApiUrl);
      const data = apiResponse.data;

      if (!data.success || !data.imageUrl) {
        throw new Error(data.error || "API returned success: false or missing image URL.");
      }

      const finalImageUrl = data.imageUrl;

      const imageDownloadResponse = await axios.get(finalImageUrl, {
          responseType: 'stream',
      });

      const cacheDir = path.join(__dirname, 'cache');
      if (!fs.existsSync(cacheDir)) fs.mkdirSync(cacheDir, { recursive: true });

      tempFilePath = path.join(cacheDir, `edited_nano_${Date.now()}.png`);

      const writer = fs.createWriteStream(tempFilePath);
      imageDownloadResponse.data.pipe(writer);

      await new Promise((resolve, reject) => {
        writer.on("finish", resolve);
        writer.on("error", (err) => {
          writer.close();
          reject(err);
        });
      });

      message.reaction("‚úÖ", event.messageID);
      await message.reply({
        attachment: fs.createReadStream(tempFilePath)
      });

    } catch (error) {
      message.reaction("‚ùå", event.messageID);

      let errorMessage = "An error occurred during image editing.";
      if (error.response && error.response.data && error.response.data.error) {
         errorMessage += ` (API Error: ${error.response.data.error})`;
      } else if (error.message) {
         errorMessage = `‚ùå ${error.message}`;
      } else if (error.code) {
         errorMessage = `‚ùå Network Error: ${error.code}`;
      }

      console.error("Edit Command Error:", error);
      message.reply(`‚ùå ${errorMessage}`);
    } finally {
      if (tempFilePath && fs.existsSync(tempFilePath)) {
          fs.unlinkSync(tempFilePath);
      }
    }
  }
};

scripts/cmds/setname.js.content:
async function checkShortCut(nickname, uid, usersData) {
	try {
		/\{userName\}/gi.test(nickname) ? nickname = nickname.replace(/\{userName\}/gi, await usersData.getName(uid)) : null;
		/\{userID\}/gi.test(nickname) ? nickname = nickname.replace(/\{userID\}/gi, uid) : null;
		return nickname;
	}
	catch (e) {
		return nickname;
	}
}

module.exports = {
	config: {
		name: "setname",
		version: "1.5",
		author: "NTKhang",
		countDown: 5,
		role: 0,
		description: {
			vi: "ƒê·ªïi bi·ªát danh c·ªßa t·∫•t c·∫£ th√†nh vi√™n trong nh√≥m chat ho·∫∑c nh·ªØng th√†nh vi√™n ƒë∆∞·ª£c tag theo m·ªôt ƒë·ªãnh d·∫°ng",
			en: "Change nickname of all members in chat or members tagged by a format"
		},
		category: "box chat",
		guide: {
			vi: {
				body: "   {pn} <nick name>: thay ƒë·ªïi bi·ªát danh c·ªßa b·∫£n th√¢n"
					+ "\n   {pn} @tags <nick name>: thay ƒë·ªïi bi·ªát danh c·ªßa nh·ªØng th√†nh vi√™n ƒë∆∞·ª£c tag"
					+ "\n   {pn} all <nick name>: thay ƒë·ªïi bi·ªát danh c·ªßa t·∫•t c·∫£ th√†nh vi√™n trong nh√≥m chat"
					+ "\n\n   V·ªõi c√°c shortcut c√≥ s·∫µn:"
					+ "\n   + {userName}: t√™n c·ªßa th√†nh vi√™n"
					+ "\n   + {userID}: ID c·ªßa th√†nh vi√™n"
					+ "\n\n   V√≠ d·ª•: (xem ·∫£nh)",
				attachment: {
					[`${__dirname}/assets/guide/setname_1.png`]: "https://i.ibb.co/gFh23zb/guide1.png",
					[`${__dirname}/assets/guide/setname_2.png`]: "https://i.ibb.co/BNWHKgj/guide2.png"
				}
			},
			en: {
				body: "   {pn} <nick name>: change nickname of yourself"
					+ "\n   {pn} @tags <nick name>: change nickname of members tagged"
					+ "\n   {pn} all <nick name>: change nickname of all members in chat"
					+ "\n\nWith available shortcuts:"
					+ "\n   + {userName}: name of member"
					+ "\n   + {userID}: ID of member"
					+ "\n\n   Example: (see image)",
				attachment: {
					[`${__dirname}/assets/guide/setname_1.png`]: "https://i.ibb.co/gFh23zb/guide1.png",
					[`${__dirname}/assets/guide/setname_2.png`]: "https://i.ibb.co/BNWHKgj/guide2.png"
				}
			}
		}
	},

	langs: {
		vi: {
			error: "ƒê√£ c√≥ l·ªói x·∫£y ra, th·ª≠ t·∫Øt t√≠nh nƒÉng li√™n k·∫øt m·ªùi trong nh√≥m v√† th·ª≠ l·∫°i sau"
		},
		en: {
			error: "An error has occurred, try turning off the invite link feature in the group and try again later"
		}
	},

	onStart: async function ({ args, message, event, api, usersData, getLang }) {
		const mentions = Object.keys(event.mentions);
		let uids = [];
		let nickname = args.join(" ");

		if (args[0] === "all" || mentions.includes(event.threadID)) {
			uids = (await api.getThreadInfo(event.threadID)).participantIDs;
			nickname = args[0] === "all" ? args.slice(1).join(" ") : nickname.replace(event.mentions[event.threadID], "").trim();
		}
		else if (mentions.length) {
			uids = mentions;
			const allName = new RegExp(
				Object.values(event.mentions)
					.map(name => name.replace(/[-\/\\^$*+?.()|[\]{}]/g, "\\$&")) // fix error when name has special characters
					.join("|")
				, "g"
			);
			nickname = nickname.replace(allName, "").trim();
		}
		else {
			uids = [event.senderID];
			nickname = nickname.trim();
		}

		try {
			const uid = uids.shift();
			await api.changeNickname(await checkShortCut(nickname, uid, usersData), event.threadID, uid);
		}
		catch (e) {
			return message.reply(getLang("error"));
		}

		for (const uid of uids)
			await api.changeNickname(await checkShortCut(nickname, uid, usersData), event.threadID, uid);
	}
};

