const { removeHomeDir, log } = global.utils;

module.exports = {
	config: {
		name: "eval",
		version: "1.6",
		author: "NTKhang",
		countDown: 5,
		role: 2,
		description: {
			vi: "Test code nhanh",
			en: "Test code quickly"
		},
		category: "owner",
		guide: {
			vi: "{pn} <ƒëo·∫°n code c·∫ßn test>",
			en: "{pn} <code to test>"
		}
	},

	langs: {
		vi: {
			error: "‚úó ƒê√£ c√≥ l·ªói x·∫£y ra:"
		},
		en: {
			error: "‚úó An error occurred:"
		}
	},

	onStart: async function ({ api, args, message, event, threadsData, usersData, dashBoardData, globalData, threadModel, userModel, dashBoardModel, globalModel, role, commandName, getLang }) {
		function output(msg) {
			if (typeof msg == "number" || typeof msg == "boolean" || typeof msg == "function")
				msg = msg.toString();
			else if (msg instanceof Map) {
				let text = `Map(${msg.size}) `;
				text += JSON.stringify(mapToObj(msg), null, 2);
				msg = text;
			}
			else if (typeof msg == "object")
				msg = JSON.stringify(msg, null, 2);
			else if (typeof msg == "undefined")
				msg = "undefined";

			message.reply(msg);
		}
		function out(msg) {
			output(msg);
		}
		function mapToObj(map) {
			const obj = {};
			map.forEach(function (v, k) {
				obj[k] = v;
			});
			return obj;
		}
		const cmd = `
		(async () => {
			try {
				${args.join(" ")}
			}
			catch(err) {
				log.err("eval command", err);
				message.send(
					"${getLang("error")}\\n" +
					(err.stack ?
						removeHomeDir(err.stack) :
						removeHomeDir(JSON.stringify(err, null, 2) || "")
					)
				);
			}
		})()`;
		eval(cmd);
	}
};

scripts/cmds/rankup.js.content:
const deltaNext = global.GoatBot.configCommands.envCommands.rank.deltaNext;
const expToLevel = exp => Math.floor((1 + Math.sqrt(1 + 8 * exp / deltaNext)) / 2);
const { drive } = global.utils;

module.exports = {
	config: {
		name: "rankup",
		version: "1.4",
		author: "NTKhang",
		countDown: 5,
		role: 0,
		description: {
			vi: "B·∫≠t/t·∫Øt th√¥ng b√°o level up",
			en: "Turn on/off level up notification"
		},
		category: "rank",
		guide: {
			en: "{pn} [on | off]"
		},
		envConfig: {
			deltaNext: 5
		}
	},

	langs: {
		vi: {
			syntaxError: "Sai c√∫ ph√°p, ch·ªâ c√≥ th·ªÉ d√πng {pn} on ho·∫∑c {pn} off",
			turnedOn: "ƒê√£ b·∫≠t th√¥ng b√°o level up",
			turnedOff: "ƒê√£ t·∫Øt th√¥ng b√°o level up",
			notiMessage: "‚òÖ‚òÖ ch√∫c m·ª´ng b·∫°n ƒë·∫°t level %1"
		},
		en: {
			syntaxError: "Syntax error, only use {pn} on or {pn} off",
			turnedOn: "Turned on level up notification",
			turnedOff: "Turned off level up notification",
			notiMessage: "‚òÖ‚òÖ Congratulations on reaching level %1"
		}
	},

	onStart: async function ({ message, event, threadsData, args, getLang }) {
		if (!["on", "off"].includes(args[0]))
			return message.reply(getLang("syntaxError"));
		await threadsData.set(event.threadID, args[0] == "on", "settings.sendRankupMessage");
		return message.reply(args[0] == "on" ? getLang("turnedOn") : getLang("turnedOff"));
	},

	onChat: async function ({ threadsData, usersData, event, message, getLang }) {
		const threadData = await threadsData.get(event.threadID);
		const sendRankupMessage = threadData.settings.sendRankupMessage;
		if (!sendRankupMessage)
			return;
		const { exp } = await usersData.get(event.senderID);
		const currentLevel = expToLevel(exp);
		if (currentLevel > expToLevel(exp - 1)) {
			let customMessage = await threadsData.get(event.threadID, "data.rankup.message");
			let isTag = false;
			let userData;
			const formMessage = {};

			if (customMessage) {
				userData = await usersData.get(event.senderID);
				customMessage = customMessage
					// .replace(/{userName}/g, userData.name)
					.replace(/{oldRank}/g, currentLevel - 1)
					.replace(/{currentRank}/g, currentLevel);
				if (customMessage.includes("{userNameTag}")) {
					isTag = true;
					customMessage = customMessage.replace(/{userNameTag}/g, `@${userData.name}`);
				}
				else {
					customMessage = customMessage.replace(/{userName}/g, userData.name);
				}

				formMessage.body = customMessage;
			}
			else {
				formMessage.body = getLang("notiMessage", currentLevel);
			}

			if (threadData.data.rankup?.attachments?.length > 0) {
				const files = threadData.data.rankup.attachments;
				const attachments = files.reduce((acc, file) => {
					acc.push(drive.getFile(file, "stream"));
					return acc;
				}, []);
				formMessage.attachment = (await Promise.allSettled(attachments))
					.filter(({ status }) => status == "fulfilled")
					.map(({ value }) => value);
			}

			if (isTag) {
				formMessage.mentions = [{
					tag: `@${userData.name}`,
					id: event.senderID
				}];
			}

			message.reply(formMessage);
		}
	}
};

scripts/events/spamDetect.js.content:
const moment = require("moment-timezone");

module.exports = {
        config: {
                name: "spamDetect",
                version: "1.0",
                author: "NeoKEX",
                category: "events"
        },

        onStart: async ({ threadsData, usersData, event, api, message }) => {
                const { threadID, senderID, isGroup } = event;

                if (!isGroup || !senderID)
                        return;

                const threadData = await threadsData.get(threadID);
                const { adminIDs } = threadData;

                // Skip if user is admin
                if (adminIDs.includes(senderID))
                        return;

                // Check and remove expired spam bans
                const spamBans = await threadsData.get(threadID, 'data.spamban', []);
                const now = Date.now();
                let hasExpired = false;

                for (let i = spamBans.length - 1; i >= 0; i--) {
                        if (spamBans[i].expireTime <= now) {
                                spamBans.splice(i, 1);
                                hasExpired = true;
                        }
                }

                if (hasExpired) {
                        await threadsData.set(threadID, spamBans, 'data.spamban');
                }

                // Check if user is currently spam banned
                const isBanned = spamBans.find(item => item.id == senderID);
                if (isBanned) {
                        if (adminIDs.includes(api.getCurrentUserID())) {
                                api.removeUserFromGroup(senderID, threadID);
                        }
                        return;
                }

                // Auto spam detection
                const autobanEnabled = await threadsData.get(threadID, 'data.spamban_auto', false);
                if (!autobanEnabled)
                        return;

                const config = await threadsData.get(threadID, 'data.spamban_config', { messages: 5, time: 10 });

                // Initialize spam tracking
                if (!global.temp.spamTracking)
                        global.temp.spamTracking = {};

                const trackKey = `${threadID}_${senderID}`;
                if (!global.temp.spamTracking[trackKey])
                        global.temp.spamTracking[trackKey] = [];

                const userMessages = global.temp.spamTracking[trackKey];
                const currentTime = Date.now();

                // Add current message
                userMessages.push(currentTime);

                // Remove messages older than detection window
                const timeWindow = config.time * 1000;
                global.temp.spamTracking[trackKey] = userMessages.filter(time => currentTime - time < timeWindow);

                // Check if spam threshold exceeded
                if (global.temp.spamTracking[trackKey].length >= config.messages) {
                        // Auto-ban for 2 hours (default)
                        const duration = 7200000; // 2h
                        const expireTime = Date.now() + duration;

                        const banData = {
                                id: senderID,
                                expireTime,
                                bannedBy: 'AUTO',
                                bannedAt: Date.now()
                        };

                        spamBans.push(banData);
                        await threadsData.set(threadID, spamBans, 'data.spamban');

                        const userName = await usersData.getName(senderID);
                        message.send(`‚ö† | ${userName} has been automatically banned for spam (2 hours)`);

                        // Clear tracking for this user
                        delete global.temp.spamTracking[trackKey];

                        // Kick if bot has admin
                        if (adminIDs.includes(api.getCurrentUserID())) {
                                api.removeUserFromGroup(senderID, threadID);
                        }
                }
        }
};

