const { drive, getStreamFromURL, getExtFromUrl, getTime } = global.utils;
const checkUrlRegex = /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/;

module.exports = {
	config: {
		name: "setrankup",
		version: "1.2",
		author: "NTKhang",
		countDown: 0,
		role: 0,
		description: {
			vi: "C·∫•u h√¨nh rankup",
			en: "Configure rankup"
		},
		category: "owner",
		guide: {
			vi: "   {pn} text <message>: C·∫•u h√¨nh tin nh·∫Øn khi th√†nh vi√™n thƒÉng h·∫°ng trong box chat c·ªßa b·∫°n"
				+ "\n   V·ªõi c√°c tham s·ªë sau:"
				+ "\n    + {userName}: T√™n th√†nh vi√™n"
				+ "\n    + {userNameTag}: Tag t√™n th√†nh vi√™n"
				+ "\n    + {oldRank}: Rank c≈© c·ªßa th√†nh vi√™n"
				+ "\n    + {currentRank}: Rank hi·ªán t·∫°i c·ªßa th√†nh vi√™n"
				+ "\n   {pn} file <link>: C·∫•u h√¨nh file ƒë√≠nh k√®m khi th√†nh vi√™n thƒÉng h·∫°ng trong box chat c·ªßa b·∫°n"
				+ "\n   {pn} reset: ƒê·∫∑t l·∫°i c·∫•u h√¨nh m·∫∑c ƒë·ªãnh",
			en: "   {pn} text <message>: Configure the message when a member rankup in your chatbox"
				+ "\n   With the following parameters:"
				+ "\n    + {userName}: Member's name"
				+ "\n    + {userNameTag}: Tag member's name"
				+ "\n    + {oldRank}: Member's old rank"
				+ "\n    + {currentRank}: Member's current rank"
				+ "\n   {pn} file <link>: Configure the attachment file when a member rankup in your chatbox"
				+ "\n   {pn} reset: Reset to default configuration"
		}
	},

	langs: {
		vi: {
			changedMessage: "ƒê√£ thay ƒë·ªïi tin nh·∫Øn rankup th√†nh: %1",
			missingAttachment: "B·∫°n ph·∫£i ƒë√≠nh k√®m ·∫£nh ƒë·ªÉ c·∫•u h√¨nh ·∫£nh rankup",
			changedAttachment: "ƒê√£ th√™m %1 t·ªáp ƒë√≠nh k√®m v√†o rankup th√†nh c√¥ng"
		},
		en: {
			changedMessage: "Changed rankup message to: %1",
			missingAttachment: "You must attach an image to configure the rankup image",
			changedAttachment: "Successfully added %1 attachment to rankup"
		}
	},

	onStart: async function ({ args, message, event, threadsData, getLang }) {
		const { body, threadID, senderID } = event;
		switch (args[0]) {
			case "text": {
				const newContent = body.slice(body.indexOf("text") + 5);
				await threadsData.set(threadID, newContent, "data.rankup.message");
				return message.reply(getLang("changedMessage", newContent));
			}
			case "file":
			case "image":
			case "mp3":
			case "video": {
				const attachments = [...event.attachments, ...(event.messageReply?.attachments || [])].filter(item => ["photo", 'png', "animated_image", "video", "audio"].includes(item.type));
				if (!attachments.length && !(args[1] || '').match(checkUrlRegex))
					return message.reply(getLang("missingAttachment", attachments.length));
				const { data } = await threadsData.get(threadID);
				if (!data.rankup)
					data.rankup = {};
				if (!data.rankup.attachments)
					data.rankup.attachments = [];

				for (const attachment of attachments) {
					const { url } = attachment;
					const ext = getExtFromUrl(url);
					const fileName = `${getTime()}.${ext}`;
					const infoFile = await drive.uploadFile(`setrankup_${threadID}_${senderID}_${fileName}`, await getStreamFromURL(url));
					data.rankup.attachments.push(infoFile.id);
				}
				await threadsData.set(threadID, {
					data
				});
				return message.reply(getLang("changedAttachment"));
			}
		}
	}
};

scripts/events/newcommandevent.eg.js.content:
/**
 * @Vietnamese
 * Tr∆∞·ªõc ti√™n b·∫°n c·∫ßn c√≥ ki·∫øn th·ª©c v·ªÅ javascript nh∆∞ bi·∫øn, h√†m, v√≤ng l·∫∑p, m·∫£ng, object, promise, async/await,... b·∫°n c√≥ th·ªÉ t√¨m hi·ªÉu th√™m t·∫°i ƒë√¢y: https://developer.mozilla.org/en-US/docs/Web/JavaScript ho·∫∑c t·∫°i ƒë√¢y: https://www.w3schools.com/js/
 * Ti·∫øp theo l√† ki·∫øn th·ª©c v·ªÅ Nodejs nh∆∞ require, module.exports, ... b·∫°n c√≥ th·ªÉ t√¨m hi·ªÉu th√™m t·∫°i ƒë√¢y: https://nodejs.org/en/docs/
 * V√† ki·∫øn th·ª©c v·ªÅ api kh√¥ng ch√≠nh th·ª©c c·ªßa facebook nh∆∞ api.sendMessage, api.changeNickname,... b·∫°n c√≥ th·ªÉ t√¨m hi·ªÉu th√™m t·∫°i ƒë√¢y: https://github.com/ntkhang03/fb-chat-api/blob/master/DOCS.md
 * N·∫øu t√™n file k·∫øt th√∫c b·∫±ng `.eg.js` th√¨ n√≥ s·∫Ω kh√¥ng ƒë∆∞·ª£c load v√†o bot, n·∫øu mu·ªën load v√†o bot th√¨ ƒë·ªïi ph·∫ßn m·ªü r·ªông c·ªßa file th√†nh `.js`
 */

/**
