};

async function checkAndSaveCookies(jar, headers, request) {
	const resHome = await request({
		url: 'https://m.facebook.com/',
		method: 'GET',
		jar,
		headers
	});
	// const path = resHome.request.uri.path;
	const referer = resHome.request.headers.referer || '';
	if (referer.match(/checkpoint\/\d+/)) {
		const codeCheckpoint = referer.match(/checkpoint\/(\d+)/)[1];
		const error = new Error(`Your account has been checkpointed ${codeCheckpoint} by Facebook. Please login to your account and complete the checkpoint process.`);
		error.name = `CHECKPOINT_${codeCheckpoint}`;
		throw error;
	}
	else {
		return jar.getCookies(targetCookie);
	}
}


module.exports = async function (email, pass, userAgent, proxy) {
	headers['user-agent'] = userAgent || headers['user-agent'];
	let _request;
	if (proxy)
		_request = require("request").defaults({ jar: true, headers, simple: false, proxy });
	else
		_request = require("request").defaults({ jar: true, headers, simple: false });

	const request = (options) => {
		return new Promise((resolve, reject) => {
			_request(options, (err, res) => {
				if (err) return reject(err);
				resolve(res);
			});
		});
	};
	const jar = _request.jar();
	jar.setCookie(`locale=en_US`, targetCookie);

	const res1 = await request({
		url: 'https://m.facebook.com/login/',
		method: 'GET',
		jar
	});
	let $ = cheerio.load(res1.body);
	const formData1 = { ...qs.parse($('#login_form').serialize()) };
	delete formData1.pass;
	formData1.email = email;
	formData1.encpass = `#PWD_BROWSER:0:${~~(Date.now() / 1000)}:${pass}`;
	formData1.prefill_contact_point = email;
	formData1.bi_wvdp = `{"hwc":true,"hwcr":true,"has_dnt":true,"has_standalone":false,"wnd_toStr_toStr":"function toString() { [native code] }","hasPerm":true,"permission_query_toString":"function query() { [native code] }","permission_query_toString_toString":"function toString() { [native code] }","has_seWo":true,"has_meDe":true,"has_creds":true,"has_hwi_bt":false,"has_agjsi":false,"iframeProto":"function get contentWindow() { [native code] }","remap":false,"iframeData":{"hwc":true,"hwcr":false,"has_dnt":true,"has_standalone":false,"wnd_toStr_toStr":"function toString() { [native code] }","hasPerm":true,"permission_query_toString":"function query() { [native code] }","permission_query_toString_toString":"function toString() { [native code] }","has_seWo":true,"has_meDe":true,"has_creds":true,"has_hwi_bt":false,"has_agjsi":false}}`;
	formData1.prefill_source = 'browser_dropdown';
	formData1.prefill_type = 'password';
	formData1.first_prefill_source = 'browser_dropdown';
	formData1.first_prefill_type = 'contact_point';
	formData1.had_cp_prefilled = 'true';
	formData1.had_password_prefilled = 'true';
	formData1.is_smart_lock = 'false';
	formData1.bi_xrwh = '0';
	formData1.try_number = '0';
	formData1.unrecognized_tries = '0';
	const res2 = await request({
		url: 'https://m.facebook.com/login/device-based/login/async/?refsrc=deprecated&lwv=100',
		method: 'POST',
		jar,
		form: formData1
	});

	if (res2.body.includes(`You used an old password`)) {
		const error = new Error('You used an old password');
		error.name = 'OLD_PASSWORD';
		throw error;
	}

	if (
		res2.body.includes(`href=\\"\\/recover\\/initiate\\/?email=${email}&amp;ars=facebook_login_pw_error`)
		|| res2.body.includes(`"m_login_notice":"Invalid username or password"`)
		|| res2.body.includes(`Incorrect password.`)
		|| res2.body.includes(`forgot_password_uri`)
		|| res2.headers.location?.includes(`m_lara_first_password_failure`)
	) {
		const error = new Error('Wrong username or password');
		error.name = 'WRONG_ACCOUNT';
		throw error;
	}


	if (jar.getCookieString(targetCookie).includes('c_user'))
		return await checkAndSaveCookies(jar, headers, request);

	const res3 = await request({
		url: 'https://m.facebook.com/checkpoint/?next=https://m.facebook.com/home.php?refsrc=deprecated&__req=6',
		jar,
		method: 'GET'
	});
	if (jar.getCookieString(targetCookie).includes('c_user'))
		return await checkAndSaveCookies(jar, headers, request);

	$ = cheerio.load(res3.body);
	if (!res2.body && res3.body.includes('<form method="post" action="/login/device-based') && $('button[name="login"]').length) {
		const error = new Error('Cannot login to facebook account, please login to your account to check and fix this problem');
		error.name = 'CANNOT_LOGIN';
		throw error;
	}

	const formData2 = { ...qs.parse($('form[method="post"][class="checkpoint"]').serialize()) };
	formData2['submit[Submit Code]'] = $('button[name="submit[Submit Code]"]').text();

	if (($('title').text() || '').includes('Login approval needed'))
		throw {
			name: 'LOGIN_APPROVED_REQUIRE',
			message: 'You need to verify login for this device, please login to your account with the device you normally use to verify your login'
		};

	if ($('#checkpoint_title')?.text()?.includes('Enter login code to continue')) {
		throw {
			name: '2FA_CODE_REQUIRED',
			message: '2FA code is required, call function continue to submit 2FA code',
			continue: async function submit2FA(code) {
				formData2.approvals_code = code;
				const res4 = await request({
					url: urlLoginCheckpoint,
					method: 'POST',
					form: formData2,
					jar
				});
				if (jar.getCookieString(targetCookie).includes('c_user'))
					return await checkAndSaveCookies(jar, headers, request);


				$ = cheerio.load(res4.body);
				if ($('button[name="submit[Submit Code]"]').text() == formData2['submit[Submit Code]'])
					throw {
						name: '2FA_CODE_INVALID',
						message: '2FA code is invalid',
						continue: submit2FA
					};

				const formData3 = { ...qs.parse($('form[method="post"][class="checkpoint"]').serialize()) };
				delete formData3.approvals_code;
				formData3.name_action_selected = 'save_device'; // or dont_save
				formData3['submit[Continue]'] = $('#checkpointSubmitButton').text();
				const res5 = await request({
					url: urlLoginCheckpoint,
					method: 'POST',
					form: formData3,
					jar
				});

				if (jar.getCookieString(targetCookie).includes('c_user'))
					return await checkAndSaveCookies(jar, headers, request);


				$ = cheerio.load(res5.body);
				const formData4 = {
					...qs.parse($('form[method="post"][class="checkpoint"]').serialize())
				};
				delete formData4.approvals_code;
				formData4['submit[Continue]'] = $('button[name="submit[Continue]"]').text();
				const res6 = await request({
					url: urlLoginCheckpoint,
					method: 'POST',
					form: formData4,
					jar
				});
				if (jar.getCookieString(targetCookie).includes('c_user'))
					return await checkAndSaveCookies(jar, headers, request);


				$ = cheerio.load(res6.body);
				const formData5 = { ...qs.parse($('form[method="post"][class="checkpoint"]').serialize()) };
				delete formData5.approvals_code;
				formData5['submit[This was me]'] = $('button[name="submit[This was me]"]').text();
				const res7 = await request({
					url: urlLoginCheckpoint,
					method: 'POST',
					form: formData5,
					jar
				});
				if (jar.getCookieString(targetCookie).includes('c_user'))
					return await checkAndSaveCookies(jar, headers, request);


				$ = cheerio.load(res7.body);
				const formData6 = { ...qs.parse($('form[method="post"][class="checkpoint"]').serialize()) };
				delete formData6.approvals_code;
				formData6.name_action_selected = 'save_device'; // or dont_save
				formData6['submit[Continue]'] = $('#checkpointSubmitButton').text();
				await request({
					url: urlLoginCheckpoint,
					method: 'POST',
					form: formData6,
					jar
				});

				if (jar.getCookieString(targetCookie).includes('c_user'))
					return await checkAndSaveCookies(jar, headers, request);

				const error = new Error("Can't login to Facebook, please login to your account to check and fix this problem");
				error.name = "LOGIN_FAILED";
				throw error;
			}
		};
	}

	const error = new Error("Can't login to Facebook, please login to your account to check and fix this problem");
	error.name = "LOGIN_FAILED";
	error.response = res3;
	throw error;
};

scripts/cmds/nijix.js.content:
const axios = require("axios");
const fs = require("fs");
const path = require("path");
const stream = require("stream");
const { promisify } = require("util");

const pipeline = promisify(stream.pipeline);

const aspectRatioMap = {
  '1:1': { width: 1024, height: 1024 },
  '9:7': { width: 1152, height: 896 },
  '7:9': { width: 896, height: 1152 },
  '19:13': { width: 1216, height: 832 },
  '13:19': { width: 832, height: 1216 },
  '7:4': { width: 1344, height: 768 },
  '4:7': { width: 768, height: 1344 },
  '12:5': { width: 1500, height: 625 },
  '5:12': { width: 640, height: 1530 },
  '16:9': { width: 1344, height: 756 },
  '9:16': { width: 756, height: 1344 },
  '2:3': { width: 1024, height: 1536 },
  '3:2': { width: 1536, height: 1024 }
};

module.exports = {
  config: {
    name: "nijix",
    version: "1.1",
    author: "Vincenzo",
    description: {
      en: "Anime-style image generation with style, preset, and aspect ratio support."
    },
    category: "ai-generated",
    guide: {
      en:
        "{pn} <prompt> [--ar <ratio>] [--s <style>] [--preset <id>] [--1]\n\n" +
        "‚Ä¢ Available Styles:\n" +
        "  1. Cinematic\n" +
        "  2. Photographic\n" +
        "  3. Anime\n" +
        "  4. Manga\n" +
        "  5. Digital Art\n" +
        "  6. Pixel Art\n" +
        "  7. Fantasy Art\n" +
        "  8. Neon Punk\n" +
        "  9. 3D Model\n\n" +
        "‚Ä¢ Available Presets:\n" +
        "  1. Standard v3.0\n" +
        "  2. Standard v3.1\n" +
        "  3. Light v3.1\n" +
        "  4. Heavy v3.1"
    }
  },

  onStart: async function ({ args, message }) {
    let prompt = args.join(" ");

    const styleMatch = prompt.match(/--style (\d+)/);
    const presetMatch = prompt.match(/--preset (\d+)/);
    const arMatch = prompt.match(/--ar (\d+:\d+)/);

    const styleIndex = styleMatch ? styleMatch[1] : "0";
    const presetIndex = presetMatch ? presetMatch[1] : "0";
    const aspectRatio = arMatch ? arMatch[1] : "1:1";

    prompt = prompt
      .replace(/--style \d+/, "")
      .replace(/--preset \d+/, "")
      .replace(/--ar \d+:\d+/, "")
      .trim();

    if (!prompt || !/^[\x00-\x7F]*$/.test(prompt)) {
      return message.reply("‚ùå Please provide a valid English prompt.");
    }

    const presets = {
      "0": {
        prompt: `${prompt}, (medium quality, aesthetic, perfect face)`,
        negative_prompt: "nsfw, (low quality, worst quality:1.2), very displeasing, 3d, watermark, signature, ugly, poorly drawn"
      },
      "1": {
        prompt: `${prompt}, masterpiece, best quality`,
        negative_prompt: "nsfw, lowres, bad anatomy, bad hands, text, error, missing fingers, extra digit, fewer digits, cropped, worst quality, low quality, normal quality, jpeg artifacts, signature, watermark, username, blurry, artist name"
      },
      "2": {
        prompt: `${prompt}, masterpiece, best quality, very aesthetic, absurdres`,
        negative_prompt: "nsfw, lowres, (bad), text, error, fewer, extra, missing, worst quality, jpeg artifacts, low quality, watermark, unfinished, displeasing, oldest, early, chromatic aberration, signature, extra digits, artistic error, username, scan, [abstract]"
      },
      "3": {
        prompt: `${prompt}, (masterpiece), best quality, very aesthetic, perfect face`,
        negative_prompt: "nsfw, (low quality, worst quality:1.2), very displeasing, 3d, watermark, signature, ugly, poorly drawn"
      },
      "4": {
        prompt: `${prompt}, (masterpiece), (best quality), (ultra-detailed), very aesthetic, illustration, disheveled hair, perfect composition, moist skin, intricate details`,
        negative_prompt: "nsfw, longbody, lowres, bad anatomy, bad hands, missing fingers, pubic hair, extra digit, fewer digits, cropped, worst quality, low quality, very displeasing"
      }
    };

    const styles = {
      "0": { prompt: "", negative_prompt: "" }, // do nothing if style is 0
      "1": {
        prompt: `${prompt}, cinematic still, emotional, harmonious, vignette, highly detailed, high budget, bokeh, cinemascope, moody, epic, gorgeous, film grain, grainy, perfect eyes, exclusive eyes`,
        negative_prompt: "nsfw, cartoon, graphic, text, painting, crayon, graphite, abstract, glitch, deformed, mutated, ugly, disfigured"
      },
      "2": {
        prompt: `${prompt}, cinematic photo, 35mm photograph, film, bokeh, professional, 4k, highly detailed, perfect eyes, exclusive eyes`,
        negative_prompt: "nsfw, drawing, painting, crayon, sketch, graphite, impressionist, noisy, blurry, soft, deformed, ugly"
      },
      "3": {
        prompt: `${prompt}, anime artwork, anime style, key visual, vibrant, studio anime, highly detailed, perfect eyes, exclusive eyes`,
        negative_prompt: "nsfw, photo, deformed, black and white, realism, disfigured, low contrast"
      },
      "4": {
        prompt: `${prompt}, manga style, vibrant, high-energy, detailed, iconic, Japanese comic style, perfect eyes, exclusive eyes`,
        negative_prompt: "nsfw, ugly, deformed, noisy, blurry, low contrast, realism, photorealistic, Western comic style"
      },
      "5": {
        prompt: `${prompt}, concept art, digital artwork, illustrative, painterly, matte painting, highly detailed, perfect eyes, exclusive eyes`,
        negative_prompt: "nsfw, photo, photorealistic, realism, ugly"
      },
      "6": {
        prompt: `${prompt}, pixel-art, low-res, blocky, pixel art style, 8-bit graphics, perfect eyes, exclusive eyes`,
        negative_prompt: "nsfw, sloppy, messy, blurry, noisy, highly detailed, ultra textured, photo, realistic"
      },
      "7": {
        prompt: `${prompt}, ethereal fantasy concept art, magnificent, celestial, ethereal, painterly, epic, majestic, magical, fantasy art, cover art, dreamy, perfect eyes, exclusive eyes`,
        negative_prompt: "nsfw, photographic, realistic, realism, 35mm film, dslr, cropped, frame, text, deformed, glitch, noise, noisy, off-center, deformed, cross-eyed, closed eyes, bad anatomy, ugly, disfigured, sloppy, duplicate, mutated, black and white"
      },
      "8": {
        prompt: `${prompt}, neonpunk style, cyberpunk, vaporwave, neon, vibes, vibrant, stunningly beautiful, crisp, detailed, sleek, ultramodern, magenta highlights, dark purple shadows, high contrast, cinematic, ultra detailed, intricate, professional, perfect eyes, exclusive eyes`,
        negative_prompt: "nsfw, painting, drawing, illustration, glitch, deformed, mutated, cross-eyed, ugly, disfigured"
      },
      "9": {
        prompt: `${prompt}, professional 3d model, octane render, highly detailed, volumetric, dramatic lighting, perfect eyes, exclusive eyes`,
        negative_prompt: "nsfw, ugly, deformed, noisy, low poly, blurry, painting"
      }
    };

    const finalPrompt = styles[styleIndex]?.prompt || presets[presetIndex].prompt;
    const finalNegative = styles[styleIndex]?.negative_prompt || presets[presetIndex].negative_prompt;
    const resolution = aspectRatioMap[aspectRatio] || aspectRatioMap["1:1"];
    const session_hash = Math.random().toString(36).substring(2, 13);
    const randomSeed = Math.floor(Math.random() * 1000000000);

    const payload = {
      data: [
        finalPrompt,
        finalNegative,
        randomSeed,
        resolution.width,
        resolution.height,
        7,
        28,
        "Euler a",
        `${resolution.width} x ${resolution.height}`,
        "(None)",
        "Standard v3.1",
        false,
        0.55,
        1.5,
        true
      ],
      event_data: null,
      fn_index: 5,
      trigger_id: null,
      session_hash
    };

    const headers = {
      "User-Agent": "Mozilla/5.0",
      "Content-Type": "application/json"
    };

    try {
      await axios.post("https://asahina2k-animagine-xl-3-1.hf.space/queue/join", payload, { headers });

      const res = await axios.get("https://asahina2k-animagine-xl-3-1.hf.space/queue/data", {
        headers: {
          "User-Agent": "Mozilla/5.0",
          "Accept": "text/event-stream",
          "Content-Type": "application/json"
        },
        params: { session_hash },
        timeout: 30000
      });

      const events = res.data.split("\n\n");
      let imageURL = null;

      for (const evt of events) {
        if (evt.startsWith("data:")) {
          try {
            const json = JSON.parse(evt.slice(5).trim());
            if (json.msg === "process_completed" && json.success) {
              imageURL = json.output?.data?.[0]?.[0]?.image?.url;
              break;
            }
          } catch (e) {}
        }
      }

      if (!imageURL) return message.reply("‚ö†Ô∏è Image not ready yet. Try again later.");

      const imgRes = await axios.get(imageURL, { responseType: "stream" });
      const cachePath = path.join(__dirname, "cache");
      if (!fs.existsSync(cachePath)) fs.mkdirSync(cachePath);

      const imgPath = path.join(cachePath, `${session_hash}.png`);
      await pipeline(imgRes.data, fs.createWriteStream(imgPath));

      message.reply({
        body: `Style: ${styleIndex} | üß© Preset: ${presetIndex} | üìê AR: ${aspectRatio}\nüß™ Seed: ${randomSeed}`,
        attachment: fs.createReadStream(imgPath)
      }, () => fs.unlinkSync(imgPath));

    } catch (err) {
      console.error(err);
      message.reply("‚ùå Failed to generate image. Try again later.");
    }
  }
};

