								query = query.includes("?feature=share") ? query.replace("?feature=share", "") : query;

								const checkurl = /^(?:https?:\/\/)?(?:m\.|www\.)?(?:youtu\.be\/|youtube\.com\/(?:embed\/|v\/|watch\?v=|watch\?.+&v=|shorts\/))((\w|-){11})(?:\S+)?$/;
								const urlYtb = checkurl.test(query);

