			unloadedError: "‚úó | Unload event command \"%1\" th·∫•t b·∫°i v·ªõi l·ªói\n%2: %3",
			missingUrlCodeOrFileName: "‚ö† | Vui l√≤ng nh·∫≠p v√†o url ho·∫∑c code v√† t√™n file l·ªánh b·∫°n mu·ªën c√†i ƒë·∫∑t",
			missingUrlOrCode: "‚ö† | Vui l√≤ng nh·∫≠p v√†o url ho·∫∑c code c·ªßa t·ªáp l·ªánh b·∫°n mu·ªën c√†i ƒë·∫∑t",
			missingFileNameInstall: "‚ö† | Vui l√≤ng nh·∫≠p v√†o t√™n file ƒë·ªÉ l∆∞u l·ªánh (ƒëu√¥i .js)",
			invalidUrlOrCode: "‚ö† | Kh√¥ng th·ªÉ l·∫•y ƒë∆∞·ª£c m√£ l·ªánh",
			alreadExist: "‚ö† | File l·ªánh ƒë√£ t·ªìn t·∫°i, b·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën ghi ƒë√® l√™n file l·ªánh c≈© kh√¥ng?\nTh·∫£ c·∫£m x√∫c b·∫•t k√¨ v√†o tin nh·∫Øn n√†y ƒë·ªÉ ti·∫øp t·ª•c",
			installed: "‚úì | ƒê√£ c√†i ƒë·∫∑t event command \"%1\" th√†nh c√¥ng, file l·ªánh ƒë∆∞·ª£c l∆∞u t·∫°i %2",
			installedError: "‚úó | C√†i ƒë·∫∑t event command \"%1\" th·∫•t b·∫°i v·ªõi l·ªói\n%2: %3",
			missingFile: "‚ö† | Kh√¥ng t√¨m th·∫•y t·ªáp l·ªánh \"%1\"",
			invalidFileName: "‚ö† | T√™n t·ªáp l·ªánh kh√¥ng h·ª£p l·ªá",
			unloadedFile: "‚úì | ƒê√£ unload l·ªánh \"%1\""
		},
		en: {
			missingFileName: "‚ö† | Please enter the command name you want to reload",
			loaded: "‚úì | Loaded event command \"%1\" successfully",
			loadedError: "‚úó | Loaded event command \"%1\" failed with error\n%2: %3",
			loadedSuccess: "‚úì | Loaded \"%1\" event command successfully",
			loadedFail: "‚úó | Loaded event command \"%1\" failed\n%2",
			missingCommandNameUnload: "‚ö† | Please enter the command name you want to unload",
			unloaded: "‚úì | Unloaded event command \"%1\" successfully",
			unloadedError: "‚úó | Unloaded event command \"%1\" failed with error\n%2: %3",
			missingUrlCodeOrFileName: "‚ö† | Please enter the url or code and command file name you want to install",
			missingUrlOrCode: "‚ö† | Please enter the url or code of the command file you want to install",
			missingFileNameInstall: "‚ö† | Please enter the file name to save the command (with .js extension)",
			invalidUrlOrCode: "‚ö† | Unable to get command code",
			alreadExist: "‚ö† | The command file already exists, are you sure you want to overwrite the old command file?\nReact to this message to continue",
			installed: "‚úì | Installed event command \"%1\" successfully, the command file is saved at %2",
			installedError: "‚úó | Installed event command \"%1\" failed with error\n%2: %3",
			missingFile: "‚ö† | File \"%1\" not found",
			invalidFileName: "‚ö† | Invalid file name",
			unloadedFile: "‚úì | Unloaded command \"%1\""
		}
	},

	onStart: async ({ args, message, api, threadModel, userModel, dashBoardModel, globalModel, threadsData, usersData, dashBoardData, globalData, commandName, event, getLang }) => {
		const { configCommands } = global.GoatBot;
		const { log, loadScripts } = global.utils;

		if (args[0] == "load" && args.length == 2) {
			if (!args[1])
				return message.reply(getLang("missingFileName"));
			const infoLoad = loadScripts("events", args[1], log, configCommands, api, threadModel, userModel, dashBoardModel, globalModel, threadsData, usersData, dashBoardData, globalData, getLang);
			infoLoad.status == "success" ?
				message.reply(getLang("loaded", infoLoad.name)) :
				message.reply(getLang("loadedError", infoLoad.name, infoLoad.error, infoLoad.message));
		}
		else if ((args[0] || "").toLowerCase() == "loadall" || (args[0] == "load" && args.length > 2)) {
			const allFile = args[0].toLowerCase() == "loadall" ?
				fs.readdirSync(path.join(__dirname, "..", "events"))
					.filter(file =>
						file.endsWith(".js") &&
						!file.match(/(eg)\.js$/g) &&
						(process.env.NODE_ENV == "development" ? true : !file.match(/(dev)\.js$/g)) &&
						!configCommands.commandEventUnload?.includes(file)
					)
					.map(item => item = item.split(".")[0]) :
				args.slice(1);
			const arraySucces = [];
			const arrayFail = [];
			for (const fileName of allFile) {
				const infoLoad = loadScripts("events", fileName, log, configCommands, api, threadModel, userModel, dashBoardModel, globalModel, threadsData, usersData, dashBoardData, globalData, getLang);
				infoLoad.status == "success" ?
					arraySucces.push(fileName) :
					arrayFail.push(`${fileName} => ${infoLoad.error.name}: ${infoLoad.error.message}`);
			}
			let msg = "";
			if (arraySucces.length > 0)
				msg += getLang("loadedSuccess", arraySucces.length) + '\n';
			if (arrayFail.length > 0)
				msg += (msg ? '\n' : '') + getLang("loadedFail", arrayFail.length, "!" + arrayFail.join("\n! "));
			message.reply(msg);
		}
		else if (args[0] == "unload") {
			if (!args[1])
				return message.reply(getLang("missingCommandNameUnload"));
			const infoUnload = global.utils.unloadScripts("events", args[1], configCommands, getLang);
			infoUnload.status == "success" ?
				message.reply(getLang("unloaded", infoUnload.name)) :
				message.reply(getLang("unloadedError", infoUnload.name, infoUnload.error.name, infoUnload.error.message));
		}
		else if (args[0] == "install") {
			let url = args[1];
			let fileName = args[2];
			let rawCode;

			if (!url || !fileName)
				return message.reply(getLang("missingUrlCodeOrFileName"));

			if (url.endsWith(".js")) {
				const tmp = fileName;
				fileName = url;
				url = tmp;
			}

			if (url.match(/(https?:\/\/(?:www\.|(?!www)))/)) {
				if (!fileName || !fileName.endsWith(".js"))
					return message.reply(getLang("missingFileNameInstall"));

				const domain = getDomain(url);
				if (!domain)
					return message.reply(getLang("invalidUrl"));

				if (domain == "pastebin.com") {
					const regex = /https:\/\/pastebin\.com\/(?!raw\/)(.*)/;
					if (url.match(regex))
						url = url.replace(regex, "https://pastebin.com/raw/$1");
					if (url.endsWith("/"))
						url = url.slice(0, -1);
				}
				else if (domain == "github.com") {
					const regex = /https:\/\/github\.com\/(.*)\/blob\/(.*)/;
					if (url.match(regex))
						url = url.replace(regex, "https://raw.githubusercontent.com/$1/$2");
				}

				rawCode = (await axios.get(url)).data;

				if (domain == "savetext.net") {
					const $ = cheerio.load(rawCode);
					rawCode = $("#content").text();
				}
			}
			else {
				if (args[args.length - 1].endsWith(".js")) {
					fileName = args[args.length - 1];
					rawCode = event.body.slice(event.body.indexOf('install') + 7, event.body.indexOf(fileName) - 1);
				}
				else if (args[1].endsWith(".js")) {
					fileName = args[1];
					rawCode = event.body.slice(event.body.indexOf(fileName) + fileName.length + 1);
				}
				else
					return message.reply(getLang("missingFileNameInstall"));
			}
			if (!rawCode)
				return message.reply(getLang("invalidUrlOrCode"));
			if (fs.existsSync(path.join(__dirname, "..", "events", fileName)))
				return message.reply(getLang("alreadExist"), (err, info) => {
					global.GoatBot.onReaction.set(info.messageID, {
						commandName,
						messageID: info.messageID,
						type: "install",
						author: event.senderID,
						data: {
							fileName,
							rawCode
						}
					});
				});
			else {
				const infoLoad = loadScripts("events", fileName, log, configCommands, api, threadModel, userModel, dashBoardModel, globalModel, threadsData, usersData, dashBoardData, globalData, getLang, rawCode);
				infoLoad.status == "success" ?
					message.reply(getLang("installed", infoLoad.name, path.join(__dirname, fileName).replace(process.cwd(), ""))) :
					message.reply(getLang("installedError", infoLoad.name, infoLoad.error.name, infoLoad.error.message));
			}
		}
		else
			message.SyntaxError();
	},

	onReaction: async function ({ Reaction, message, event, api, threadModel, userModel, dashBoardModel, globalModel, threadsData, usersData, dashBoardData, globalData, getLang }) {
		const { author, messageID, data: { fileName, rawCode } } = Reaction;
		if (event.userID != author)
			return;
		const { configCommands } = global.GoatBot;
		const { log, loadScripts } = global.utils;
		const infoLoad = loadScripts("cmds", fileName, log, configCommands, api, threadModel, userModel, dashBoardModel, globalModel, threadsData, usersData, dashBoardData, globalData, getLang, rawCode);
		infoLoad.status == "success" ?
			message.reply(getLang("installed", infoLoad.name, path.join(__dirname, '..', 'events', fileName).replace(process.cwd(), ""), () => message.unsend(messageID))) :
			message.reply(getLang("installedError", infoLoad.name, infoLoad.error.name, infoLoad.error.message, () => message.unsend(messageID)));
	}
};

