const axios = require("axios");

module.exports = {
	config: {
		name: "emojimix",
		version: "1.4",
		author: "NTKhang",
		countDown: 5,
		role: 0,
		description: {
			vi: "Mix 2 emoji l·∫°i v·ªõi nhau",
			en: "Mix 2 emoji together"
		},
		guide: {
			vi: "   {pn} <emoji1> <emoji2>"
				+ "\n   V√≠ d·ª•:  {pn} ü§£ ü•∞",
			en: "   {pn} <emoji1> <emoji2>"
				+ "\n   Example:  {pn} ü§£ ü•∞"
		},
		category: "fun"
	},

	langs: {
		vi: {
			error: "R·∫•t ti·∫øc, emoji %1 v√† %2 kh√¥ng mix ƒë∆∞·ª£c",
			success: "Emoji %1 v√† %2 mix ƒë∆∞·ª£c %3 ·∫£nh"
		},
		en: {
			error: "Sorry, emoji %1 and %2 can't mix",
			success: "Emoji %1 and %2 mix %3 images"
		}
	},

	onStart: async function ({ message, args, getLang }) {
		const readStream = [];
		const emoji1 = args[0];
		const emoji2 = args[1];

		if (!emoji1 || !emoji2)
			return message.SyntaxError();

		const generate1 = await generateEmojimix(emoji1, emoji2);
		const generate2 = await generateEmojimix(emoji2, emoji1);

		if (generate1)
			readStream.push(generate1);
		if (generate2)
			readStream.push(generate2);

		if (readStream.length == 0)
			return message.reply(getLang("error", emoji1, emoji2));

		message.reply({
			body: getLang("success", emoji1, emoji2, readStream.length),
			attachment: readStream
		});
	}
};



async function generateEmojimix(emoji1, emoji2) {
	try {
		const { data: response } = await axios.get("https://goatbotserver.onrender.com/taoanhdep/emojimix", {
			params: {
				emoji1,
				emoji2
			},
			responseType: "stream"
		});
		response.path = `emojimix${Date.now()}.png`;
		return response;
	}
	catch (e) {
		return null;
	}
}

