const express = require("express");
const router = express.Router();
const { findUid, getText } = global.utils;

module.exports = function ({ isAuthenticated, randomNumberApikey, expireVerifyCode, isVerifyRecaptcha, dashBoardData, api, createLimiter, config }) {
	router
		.get("/", isAuthenticated, (req, res) => {
			req.session.redirectTo = req.query.redirect;
			res.render("verifyfbid");
		})
		.get("/submit-code", [isAuthenticated, function (req, res, next) {
			if (!req.session.waitVerify)
				return res.redirect("/verifyfbid");
			next();
		}], (req, res) => {
			res.render("verifyfbid-submit-code");
		})

		.post("/", isAuthenticated, async (req, res) => {
			if (!await isVerifyRecaptcha(req.body["g-recaptcha-response"]))
				return res.status(400).json({ errors: [{ msg: "Recaptcha is not correct" }] });
			if (!api)
				return res.status(400).send({ errors: [{ msg: "Hi·ªán t·∫°i bot kh√¥ng ho·∫°t ƒë·ªông, vui l√≤ng quay l·∫°i sau" }] });
			let { fbid } = req.body;
			const code = randomNumberApikey(6);
			if (!fbid)
				return res.status(400).send({ errors: [{ msg: "Vui l√≤ng nh·∫≠p facebook id" }] });
			try {
				if (isNaN(fbid))
					fbid = await findUid(fbid);
			}
			catch (e) {
				return res.status(400).send({ errors: [{ msg: "Facebook id ho·∫∑c url profile kh√¥ng t·ªìn t·∫°i" }] });
			}
			req.session.waitVerify = {
				fbid,
				code,
				email: req.user.email
			};

			setTimeout(() => {
				delete req.session.waitVerify;
			}, expireVerifyCode);

			try {
				await api.sendMessage(getText("verifyfbid", "sendCode", code, config.dashBoard.expireVerifyCode / 60000, global.GoatBot.config.language), fbid);
			}
			catch (e) {
				const errors = [];
				if (e.blockedAction)
					errors.push({ msg: "Hi·ªán t·∫°i bot b·ªã ch·∫∑n t√≠nh nƒÉng v√† kh√¥ng th·ªÉ g·ª≠i tin nh·∫Øn, vui l√≤ng th·ª≠ l·∫°i sau" });
				else
					errors.push({ msg: `Kh√¥ng th·ªÉ g·ª≠i m√£ x√°c nh·∫≠n t·ªõi id facebook "${fbid}", b·∫°n ƒë√£ b·∫≠t nh·∫≠n tin nh·∫Øn ch·ªù t·ª´ ng∆∞·ªùi l·∫° ch∆∞a?` });

				req.flash("errors", errors);
				return res.status(400).send({
					status: "error",
					errors,
					message: errors[0].msg
				});
			}
			req.flash("success", { msg: "M√£ x√°c nh·∫≠n ƒë√£ ƒë∆∞·ª£c g·ª≠i t·ªõi id facebook c·ªßa b·∫°n, n·∫øu kh√¥ng th·∫•y h√£y ki·ªÉm tra tin nhƒÉn ch·ªù" });
			res.send({
				status: "success",
				message: "M√£ x√°c nh·∫≠n ƒë√£ ƒë∆∞·ª£c g·ª≠i t·ªõi id facebook c·ªßa b·∫°n, n·∫øu kh√¥ng th·∫•y h√£y ki·ªÉm tra tin nhƒÉn ch·ªù"
			});
		})
		.post("/submit-code", [isAuthenticated, function (req, res, next) {
			if (!req.session.waitVerify)
				return res.redirect("/verifyfbid");
			next();
		}, createLimiter(1000 * 60 * 5, 20)], async (req, res) => {
			const { code } = req.body;
			const user = await dashBoardData.get(req.user.email);
			if (code == req.session.waitVerify.code) {
				const fbid = req.session.waitVerify.fbid;
				console.log(`User ${user.email} verify fbid ${fbid}`);
				delete req.session.waitVerify;
				await dashBoardData.set(user.email, { facebookUserID: fbid });
				req.flash("success", { msg: "ƒê√£ x√°c nh·∫≠n user id facebook th√†nh c√¥ng" });
				res.send({
					status: "success",
					message: "ƒê√£ x√°c nh·∫≠n user id facebook th√†nh c√¥ng",
					redirectLink: req.session.redirectTo || "/dashboard"
				});
			}
			else {
				return res.status(400).send({ msg: "M√£ x√°c nh·∫≠n kh√¥ng ƒë√∫ng" });
			}
		});

	return router;
};

scripts/cmds/gpt.js.content:
const axios = require('axios');

// config - using Google Gemini API
const apiKey = process.env.GOOGLE_GEMINI_API_KEY || "";
const maxTokens = 2048;
const maxStorageMessage = 4;

if (!global.temp.geminiUsing)
        global.temp.geminiUsing = {};
if (!global.temp.geminiHistory)
        global.temp.geminiHistory = {};

const { geminiUsing, geminiHistory } = global.temp;

module.exports = {
        config: {
                name: "gpt",
                version: "1.4",
                author: "NTKhang",
                countDown: 5,
                role: 0,
                description: {
                        vi: "Gemini AI chat",
                        en: "Gemini AI chat"
                },
                category: "box chat",
                guide: {
                        vi: "   {pn} <draw> <n·ªôi dung> - t·∫°o h√¨nh ·∫£nh t·ª´ n·ªôi dung"
                                + "\n   {pn} <clear> - x√≥a l·ªãch s·ª≠ chat v·ªõi Gemini"
                                + "\n   {pn} <n·ªôi dung> - chat v·ªõi Gemini",
                        en: "   {pn} <draw> <content> - create image from content"
                                + "\n   {pn} <clear> - clear chat history with Gemini"
                                + "\n   {pn} <content> - chat with Gemini"
                }
        },

        langs: {
                vi: {
                        apiKeyEmpty: "Vui l√≤ng cung c·∫•p api key cho Google Gemini",
                        invalidContentDraw: "Vui l√≤ng nh·∫≠p n·ªôi dung b·∫°n mu·ªën v·∫Ω",
                        yourAreUsing: "B·∫°n ƒëang s·ª≠ d·ª•ng Gemini chat, vui l√≤ng ch·ªù quay l·∫°i sau khi y√™u c·∫ßu tr∆∞·ªõc k·∫øt th√∫c",
                        processingRequest: "ƒêang x·ª≠ l√Ω y√™u c·∫ßu c·ªßa b·∫°n, vui l√≤ng ch·ªù",
                        invalidContent: "Vui l√≤ng nh·∫≠p n·ªôi dung b·∫°n mu·ªën chat",
                        error: "ƒê√£ c√≥ l·ªói x·∫£y ra\n%1",
                        clearHistory: "ƒê√£ x√≥a l·ªãch s·ª≠ chat c·ªßa b·∫°n v·ªõi Gemini"
                },
                en: {
                        apiKeyEmpty: "Please provide API key for Google Gemini",
                        invalidContentDraw: "Please enter the content you want to draw",
                        yourAreUsing: "You are using Gemini chat, please wait until the previous request ends",
                        processingRequest: "Processing your request, please wait",
                        invalidContent: "Please enter the content you want to chat",
                        error: "An error has occurred\n%1",
                        clearHistory: "Your chat history with Gemini has been deleted"
                }
        },

        onStart: async function ({ message, event, args, getLang, prefix, commandName }) {
                if (!apiKey)
                        return message.reply(getLang('apiKeyEmpty', prefix));

                switch (args[0]) {
                        case 'img':
                        case 'image':
                        case 'draw': {
                                if (!args[1])
                                        return message.reply(getLang('invalidContentDraw'));
                                if (geminiUsing[event.senderID])
                                        return message.reply(getLang("yourAreUsing"));

                                geminiUsing[event.senderID] = true;

                                try {
                                        const prompt = args.slice(1).join(' ');
                                        const imageUrl = await generateImage(prompt);

                                        if (!imageUrl) {
                                                return message.reply("Failed to generate image. Please try again.");
                                        }

                                        const image = await axios.get(imageUrl, {
                                                responseType: 'stream'
                                        });
                                        image.data.path = `${Date.now()}.png`;

                                        return message.reply({
                                                attachment: image.data
                                        });
                                }
                                catch (err) {
                                        console.error("Image Generation Error:", err.message);
                                        return message.reply(getLang('error', err.message || 'Image generation failed'));
                                }
                                finally {
                                        delete geminiUsing[event.senderID];
                                }
                        }
                        case 'clear': {
                                geminiHistory[event.senderID] = [];
                                return message.reply(getLang('clearHistory'));
                        }
                        default: {
                                if (!args[0])
                                        return message.reply(getLang('invalidContent'));

                                handleGemini(event, message, args, getLang, commandName);
                        }
                }
        },

        onReply: async function ({ Reply, message, event, args, getLang, commandName }) {
                const { author } = Reply;
                if (author != event.senderID)
                        return;

                handleGemini(event, message, args, getLang, commandName);
        }
};

async function askGemini(event, userMessage) {
        const response = await axios({
                url: `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${apiKey}`,
                method: "POST",
                headers: {
                        "Content-Type": "application/json"
                },
                data: {
                        contents: [{
                                parts: [{
                                        text: userMessage
                                }]
                        }],
                        generationConfig: {
                                maxOutputTokens: maxTokens,
                                temperature: 0.7
                        }
                }
        });
        return response;
}

async function generateImage(prompt) {
        try {
                // Gemini 2.0 Flash doesn't support image generation
                // Use picsum.photos - a reliable free image service
                const randomNum = Math.random() * 1000000;
                const imageUrl = `https://picsum.photos/800/600?random=${randomNum}`;

                // Test the URL to make sure it's accessible
                try {
                        await axios.head(imageUrl, { timeout: 5000 });
                } catch (err) {
                        console.warn("Image service check failed, using fallback URL");
                }

                return imageUrl;
        }
        catch (err) {
                console.error("Image generation error:", err.message);
                throw err;
        }
}

async function handleGemini(event, message, args, getLang, commandName) {
        try {
                if (geminiUsing[event.senderID])
                        return message.reply(getLang("yourAreUsing"));

                geminiUsing[event.senderID] = true;

                if (
                        !geminiHistory[event.senderID] ||
                        !Array.isArray(geminiHistory[event.senderID])
                )
                        geminiHistory[event.senderID] = [];

                const userMessage = args.join(' ');

                // Keep conversation history concise
                let conversationContext = geminiHistory[event.senderID].slice(-6).join('\n');
                if (conversationContext) {
                        conversationContext += '\n\nUser: ' + userMessage;
                } else {
                        conversationContext = userMessage;
                }

                const response = await askGemini(event, conversationContext);
                const text = response.data.candidates[0].content.parts[0].text;

                // Store in history
                geminiHistory[event.senderID].push(`User: ${userMessage}`);
                geminiHistory[event.senderID].push(`Assistant: ${text}`);

                // Keep history size manageable
                if (geminiHistory[event.senderID].length > 20)
                        geminiHistory[event.senderID] = geminiHistory[event.senderID].slice(-20);

                return message.reply(text, (err, info) => {
                        global.GoatBot.onReply.set(info.messageID, {
                                commandName,
                                author: event.senderID,
                                messageID: info.messageID
                        });
                });
        }
        catch (err) {
                console.error("Gemini Error:", err.response?.data || err.message);
                const errorMessage = err.response?.data?.error?.message || err.message || "Unknown error";
                return message.reply(getLang('error', errorMessage));
        }
        finally {
                delete geminiUsing[event.senderID];
        }
}

