const { GoatBotApis } = global.utils;

module.exports = {
	config: {
		name: "texttoimage",
		aliases: ["midjourney", "openjourney", "text2image"],
		version: "1.3",
		author: "NTKhang",
		countDown: 5,
		role: 0,
		description: {
			uid: "T·∫°o ·∫£nh t·ª´ vƒÉn b·∫£n c·ªßa b·∫°n",
			en: "Create image from your text"
		},
		category: "info",
		guide: {
			vi: "   {pn} <prompt>: t·∫°o ·∫£nh t·ª´ vƒÉn b·∫£n c·ªßa b·∫°n"
				+ "\n    V√≠ d·ª•: {pn} mdjrny-v4 create a gta style house, gta, 4k, hyper detailed, cinematic, realistic, unreal engine, cinematic lighting, bright lights"
				+ "\n    Example: {pn} mdjrny-v4 create a gta style house, gta, 4k, hyper detailed, cinematic, realistic, unreal engine, cinematic lighting, bright lights"
		}
	},

	langs: {
		vi: {
			syntaxError: "‚ö† Vui l√≤ng nh·∫≠p prompt",
			error: "! ƒê√£ c√≥ l·ªói x·∫£y ra, vui l√≤ng th·ª≠ l·∫°i sau:\n%1",
			serverError: "! Server ƒëang qu√° t·∫£i, vui l√≤ng th·ª≠ l·∫°i sau",
			missingGoatApiKey: "! Ch∆∞a c√†i ƒë·∫∑t apikey cho GoatBot, vui l√≤ng truy c·∫≠p goatbot.tk ƒë·ªÉ l·∫•y apikey v√† c√†i ƒë·∫∑t v√†o file configCommands.json > envGlobal.goatbotApikey v√† l∆∞u l·∫°i"
		},
		en: {
			syntaxError: "‚ö† Please enter prompt",
			error: "! An error has occurred, please try again later:\n%1",
			serverError: "! Server is overloaded, please try again later",
			missingGoatApiKey: "! Not set apikey for GoatBot, please visit goatbot.tk to get apikey and set it to configCommands.json > envGlobal.goatbotApikey and save"
		}
	},

	onStart: async function ({ message, args, getLang, envGlobal }) {
		const goatBotApi = new GoatBotApis(envGlobal.goatbotApikey);
