const { config } = global.GoatBot;
const { writeFileSync } = require("fs-extra");

module.exports = {
        config: {
                name: "admin",
                version: "1.6",
                author: "NTKhang",
                countDown: 5,
                role: 2,
                description: {
                        vi: "Th√™m, x√≥a, s·ª≠a quy·ªÅn admin",
                        en: "Add, remove, edit admin role"
                },
                category: "box chat",
                guide: {
                        vi: '   {pn} [add | -a] <uid | @tag>: Th√™m quy·ªÅn admin cho ng∆∞·ªùi d√πng'
                                + '\n     {pn} [remove | -r] <uid | @tag>: X√≥a quy·ªÅn admin c·ªßa ng∆∞·ªùi d√πng'
                                + '\n     {pn} [list | -l]: Li·ªát k√™ danh s√°ch admin',
                        en: '   {pn} [add | -a] <uid | @tag>: Add admin role for user'
                                + '\n     {pn} [remove | -r] <uid | @tag>: Remove admin role of user'
                                + '\n     {pn} [list | -l]: List all admins'
                }
        },

        langs: {
                vi: {
                        added: "‚úì | ƒê√£ th√™m quy·ªÅn admin cho %1 ng∆∞·ªùi d√πng:\n%2",
                        alreadyAdmin: "\n‚ö† | %1 ng∆∞·ªùi d√πng ƒë√£ c√≥ quy·ªÅn admin t·ª´ tr∆∞·ªõc r·ªìi:\n%2",
                        missingIdAdd: "‚ö† | Vui l√≤ng nh·∫≠p ID ho·∫∑c tag ng∆∞·ªùi d√πng mu·ªën th√™m quy·ªÅn admin",
                        removed: "‚úì | ƒê√£ x√≥a quy·ªÅn admin c·ªßa %1 ng∆∞·ªùi d√πng:\n%2",
                        notAdmin: "‚ö† | %1 ng∆∞·ªùi d√πng kh√¥ng c√≥ quy·ªÅn admin:\n%2",
                        missingIdRemove: "‚ö† | Vui l√≤ng nh·∫≠p ID ho·∫∑c tag ng∆∞·ªùi d√πng mu·ªën x√≥a quy·ªÅn admin",
                        listAdmin: "‚ôî | Danh s√°ch admin:\n%1"
                },
                en: {
                        added: "‚úì | Added admin role for %1 users:\n%2",
                        alreadyAdmin: "\n‚ö† | %1 users already have admin role:\n%2",
                        missingIdAdd: "‚ö† | Please enter ID or tag user to add admin role",
                        removed: "‚úì | Removed admin role of %1 users:\n%2",
                        notAdmin: "‚ö† | %1 users don't have admin role:\n%2",
                        missingIdRemove: "‚ö† | Please enter ID or tag user to remove admin role",
                        listAdmin: "‚ôî | List of admins:\n%1"
                }
        },

        onStart: async function ({ message, args, usersData, event, getLang }) {
                switch (args[0]) {
                        case "add":
                        case "-a": {
                                if (args[1]) {
                                        let uids = [];
                                        if (Object.keys(event.mentions).length > 0)
                                                uids = Object.keys(event.mentions);
                                        else if (event.messageReply)
                                                uids.push(event.messageReply.senderID);
                                        else
                                                uids = args.filter(arg => !isNaN(arg));
                                        const notAdminIds = [];
                                        const adminIds = [];
                                        for (const uid of uids) {
                                                if (config.adminBot.includes(uid))
                                                        adminIds.push(uid);
                                                else
                                                        notAdminIds.push(uid);
                                        }

                                        config.adminBot.push(...notAdminIds);
                                        const getNames = await Promise.all(uids.map(uid => usersData.getName(uid).then(name => ({ uid, name }))));
                                        writeFileSync(global.client.dirConfig, JSON.stringify(config, null, 2));
                                        return message.reply(
                                                (notAdminIds.length > 0 ? getLang("added", notAdminIds.length, getNames.map(({ uid, name }) => `‚Ä¢ ${name} (${uid})`).join("\n")) : "")
                                                + (adminIds.length > 0 ? getLang("alreadyAdmin", adminIds.length, adminIds.map(uid => `‚Ä¢ ${uid}`).join("\n")) : "")
                                        );
                                }
                                else
                                        return message.reply(getLang("missingIdAdd"));
                        }
                        case "remove":
                        case "-r": {
                                if (args[1]) {
                                        let uids = [];
                                        if (Object.keys(event.mentions).length > 0)
                                                uids = Object.keys(event.mentions)[0];
                                        else
                                                uids = args.filter(arg => !isNaN(arg));
                                        const notAdminIds = [];
                                        const adminIds = [];
                                        for (const uid of uids) {
                                                if (config.adminBot.includes(uid))
                                                        adminIds.push(uid);
                                                else
                                                        notAdminIds.push(uid);
                                        }
                                        for (const uid of adminIds)
                                                config.adminBot.splice(config.adminBot.indexOf(uid), 1);
                                        const getNames = await Promise.all(adminIds.map(uid => usersData.getName(uid).then(name => ({ uid, name }))));
                                        writeFileSync(global.client.dirConfig, JSON.stringify(config, null, 2));
                                        return message.reply(
                                                (adminIds.length > 0 ? getLang("removed", adminIds.length, getNames.map(({ uid, name }) => `‚Ä¢ ${name} (${uid})`).join("\n")) : "")
                                                + (notAdminIds.length > 0 ? getLang("notAdmin", notAdminIds.length, notAdminIds.map(uid => `‚Ä¢ ${uid}`).join("\n")) : "")
                                        );
                                }
                                else
                                        return message.reply(getLang("missingIdRemove"));
                        }
                        case "list":
                        case "-l": {
                                const getNames = await Promise.all(config.adminBot.map(uid => usersData.getName(uid).then(name => ({ uid, name }))));
                                return message.reply(getLang("listAdmin", getNames.map(({ uid, name }) => `‚Ä¢ ${name} (${uid})`).join("\n")));
                        }
                        default:
                                return message.SyntaxError();
                }
        }
};

