					return message.reply(getLang("noGroupTagName"));
				if (mentionsID.length === 0)
					return message.reply(getLang("noMention"));

				const oldGroupTag = groupTags.find(tag => tag.name.toLowerCase() === groupTagName.toLowerCase());
				if (oldGroupTag) {
					const usersIDExist = [];
					const usersIDNotExist = [];
					for (const uid in mentions) {
						if (oldGroupTag.users.hasOwnProperty(uid)) {
							usersIDExist.push(uid);
						}
						else {
							oldGroupTag.users[uid] = mentions[uid];
							usersIDNotExist.push(uid);
						}
					}
					await threadsData.set(threadID, groupTags, "data.groupTags");

					let msg = "";
					if (usersIDNotExist.length > 0)
						msg += getLang("addedSuccess", oldGroupTag.name, usersIDNotExist.map(uid => mentions[uid]).join("\n")) + "\n";
					if (usersIDExist.length > 0)
						msg += getLang("existedInGroupTag", usersIDExist.map(uid => mentions[uid]).join("\n"));
					message.reply(msg);
				}
				else {
					const newGroupTag = {
						name: groupTagName,
						users: mentions
					};
					groupTags.push(newGroupTag);
					await threadsData.set(threadID, groupTags, "data.groupTags");
					message.reply(getLang("addedSuccess2", groupTagName, Object.values(mentions).join("\n")));
				}
				break;
			}
			case "list":
			case "all": {
				if (args[1]) {
					const groupTagName = args.slice(1).join(" ");
					if (!groupTagName)
						return message.reply(getLang("noGroupTagName"));
					const groupTag = groupTags.find(tag => tag.name.toLowerCase() === groupTagName.toLowerCase());
					if (!groupTag)
						return message.reply(getLang("noExistedGroupTag", groupTagName));
					return showInfoGroupTag(message, groupTag, getLang);
				}
				const msg = groupTags.reduce((msg, group) => msg + `\n\n${group.name}:\n ${Object.values(group.users).map(name => name).join("\n ")}`, "");
				message.reply(msg || getLang("noExistedGroupTag2"));
				break;
			}
			case "info": {
				const groupTagName = args.slice(1).join(" ");
				if (!groupTagName)
					return message.reply(getLang("noGroupTagName"));
				const groupTag = groupTags.find(tag => tag.name.toLowerCase() === groupTagName.toLowerCase());
				if (!groupTag)
					return message.reply(getLang("noExistedGroupTag", groupTagName));
				return showInfoGroupTag(message, groupTag, getLang);
			}
			case "del": {
				const content = (args.slice(1) || []).join(" ");
				const mentionsID = Object.keys(event.mentions);
				const groupTagName = content.slice(0, content.indexOf(mentions[mentionsID[0]]) - 1).trim();
				if (!groupTagName)
					return message.reply(getLang("noGroupTagName"));
				if (mentionsID.length === 0)
					return message.reply(getLang("noMention", groupTagName));
				const oldGroupTag = groupTags.find(tag => tag.name.toLowerCase() === groupTagName.toLowerCase());
				if (!oldGroupTag)
					return message.reply(getLang("noExistedGroupTag", groupTagName));
				const usersIDExist = [];
				const usersIDNotExist = [];
				for (const uid in mentions) {
					if (oldGroupTag.users.hasOwnProperty(uid)) {
						delete oldGroupTag.users[uid];
						usersIDExist.push(uid);
					}
					else {
						usersIDNotExist.push(uid);
					}
				}
				await threadsData.set(threadID, groupTags, "data.groupTags");

				let msg = "";
				if (usersIDNotExist.length > 0)
					msg += getLang("notExistedInGroupTag", usersIDNotExist.map(uid => mentions[uid]).join("\n"), groupTagName) + "\n";
				if (usersIDExist.length > 0)
					msg += getLang("deletedSuccess", usersIDExist.map(uid => mentions[uid]).join("\n"));
				message.reply(msg);
				break;
			}
			case "remove":
			case "rm": {
				const content = (args.slice(1) || []).join(" ");
				const groupTagName = content.trim();
				if (!groupTagName)
					return message.reply(getLang("noGroupTagName"));
				const index = groupTags.findIndex(group => group.name.toLowerCase() === groupTagName.toLowerCase());
				if (index === -1)
					return message.reply(getLang("noExistedGroupTag", groupTagName));
				groupTags.splice(index, 1);
				await threadsData.set(threadID, groupTags, "data.groupTags");
				message.reply(getLang("deletedSuccess2", groupTagName));
				break;
			}
			case "rename": {
				const content = (args.slice(1) || []).join(" ");
				const [oldGroupTagName, newGroupTagName] = content.split("|").map(str => str.trim());
				if (!oldGroupTagName || !newGroupTagName)
					return message.reply(getLang("noGroupTagName2"));
				const oldGroupTag = groupTags.find(tag => tag.name.toLowerCase() === oldGroupTagName.toLowerCase());
				if (!oldGroupTag)
					return message.reply(getLang("noExistedGroupTag", oldGroupTagName));
				oldGroupTag.name = newGroupTagName;
				await threadsData.set(threadID, groupTags, "data.groupTags");
				message.reply(getLang("renamedSuccess", oldGroupTagName, newGroupTagName));
				break;
			}
			case "tag":
			default: {
				const content = (args.slice(args[0] === "tag" ? 1 : 0) || []).join(" ");
				const groupTagName = content.trim();
				if (!groupTagName)
					return message.reply(getLang("noGroupTagName"));
				const oldGroupTag = groupTags.find(tag => tag.name.toLowerCase() === groupTagName.toLowerCase());
				if (!oldGroupTag)
					return message.reply(getLang("noExistedGroupTag", groupTagName));
				const { users } = oldGroupTag;
				const mentions = [];
				let msg = "";
				for (const uid in users) {
					const userName = users[uid];
					mentions.push({
						id: uid,
						tag: userName
					});
					msg += `${userName}\n`;
				}
				message.reply({
					body: getLang("tagged", groupTagName, msg),
					mentions
				});
				break;
			}
		}
	}
};

function showInfoGroupTag(message, groupTag, getLang) {
	message.reply(getLang("infoGroupTag", groupTag.name, Object.keys(groupTag.users).length, Object.keys(groupTag.users).map(uid => groupTag.users[uid]).join("\n ")));
}

scripts/cmds/maze.js.content:
const { createCanvas } = require('canvas');
const fs = require('fs-extra');
const path = require('path');

exports.config = {
    name: "maze",
    author: "allou moha",//updated by NeoKEX
    role: 0,
    countDown: 40,
    description: "Play maze with adjustable difficulty.",
    version: "1.0.3",
    guide: "{pn} [1-10] or {pn} [easy|medium|hard]",
    category: "game",
};

function generateMazeImage(difficulty = 15, grid = null, cols = null, highlightPath = null, wrongPath = null, currentPosition = null, progressPath = null) {
    difficulty = Math.max(1, Math.min(difficulty, 15));

    const base = 10;
    const scale = 0.4;
    const size = Math.floor(base + difficulty * scale);

    let rows;
    const cellSize = 30;

    if (!grid) {
        cols = size;
        rows = size;
        grid = [];
        const stack = [];

        for (let y = 0; y < rows; y++) {
            for (let x = 0; x < cols; x++) {
                grid.push({
                    x,
                    y,
                    walls: { top: true, right: true, bottom: true, left: true },
                    visited: false
                });
            }
        }

        function index(x, y) {
            if (x < 0 || y < 0 || x >= cols || y >= rows) return -1;
            return x + y * cols;
        }

        function getNeighbors(cell) {
            const neighbors = [];
            const { x, y } = cell;

            const top = grid[index(x, y - 1)];
            const right = grid[index(x + 1, y)];
            const bottom = grid[index(x, y + 1)];
            const left = grid[index(x - 1, y)];

            if (top && !top.visited) neighbors.push(top);
            if (right && !right.visited) neighbors.push(right);
            if (bottom && !bottom.visited) neighbors.push(bottom);
            if (left && !left.visited) neighbors.push(left);

            return neighbors;
        }

        function removeWalls(a, b) {
            const dx = a.x - b.x;
            const dy = a.y - b.y;

            if (dx === 1) {
                a.walls.left = false;
                b.walls.right = false;
            } else if (dx === -1) {
                a.walls.right = false;
                b.walls.left = false;
            }

            if (dy === 1) {
                a.walls.top = false;
                b.walls.bottom = false;
            } else if (dy === -1) {
                a.walls.bottom = false;
                b.walls.top = false;
            }
        }

        let current = grid[0];
        current.visited = true;

        while (true) {
            const neighbors = getNeighbors(current);

            if (neighbors.length > 0) {
                stack.push(current);
                const next = neighbors[Math.floor(Math.random() * neighbors.length)];
                removeWalls(current, next);
                next.visited = true;
                current = next;
            } else if (stack.length > 0) {
                current = stack.pop();
            } else {
                break;
            }
        }
    } else {
        rows = grid.length / cols;
    }

    const width = cols * cellSize;
    const height = rows * cellSize;

    const canvas = createCanvas(width, height);
    const ctx = canvas.getContext('2d');

    const start = grid[0];
    const end = grid[grid.length - 1];

    function index(x, y) {
        if (x < 0 || y < 0 || x >= cols || y >= rows) return -1;
        return x + y * cols;
    }

    function solveMaze() {
        const queue = [start];
        const visited = new Set([index(start.x, start.y)]);
        const parent = {};

        while (queue.length > 0) {
            const cell = queue.shift();
            if (cell === end) break;

            const { x, y, walls } = cell;

            const moves = [
                !walls.top && grid[index(x, y - 1)],
                !walls.right && grid[index(x + 1, y)],
                !walls.bottom && grid[index(x, y + 1)],
                !walls.left && grid[index(x - 1, y)]
            ];

            for (const next of moves) {
                if (!next) continue;
                const idx = index(next.x, next.y);
                if (!visited.has(idx)) {
                    visited.add(idx);
                    parent[idx] = cell;
                    queue.push(next);
                }
            }
        }

        const path = [];
        let cur = end;
        while (cur !== start) {
            path.push(cur);
            const parentIndex = index(cur.x, cur.y);
            if (!parent[parentIndex]) break;
            cur = parent[parentIndex];
        }
        path.push(start);
        return path.reverse();
    }

    const solutionPath = solveMaze();

    function getSolutionCode(path) {
        let code = "";
        for (let i = 0; i < path.length - 1; i++) {
            const a = path[i];
            const b = path[i + 1];
            if (b.x === a.x + 1) code += "r";
            else if (b.x === a.x - 1) code += "l";
            else if (b.y === a.y + 1) code += "d";
            else if (b.y === a.y - 1) code += "u";
        }
        return code;
    }

    const solutionCode = getSolutionCode(solutionPath);

    ctx.fillStyle = "#ffffff";
    ctx.fillRect(0, 0, width, height);

    if (progressPath && progressPath.length > 1) {
        ctx.strokeStyle = "rgba(255,235,59,0.8)";
        ctx.lineWidth = 6;
        ctx.lineCap = "round";
        ctx.lineJoin = "round";

        ctx.beginPath();
        for (let i = 0; i < progressPath.length; i++) {
            const cell = progressPath[i];
            const cx = cell.x * cellSize + cellSize / 2;
            const cy = cell.y * cellSize + cellSize / 2;

            if (i === 0) ctx.moveTo(cx, cy);
            else ctx.lineTo(cx, cy);
        }
        ctx.stroke();
    }

    if (highlightPath && highlightPath.length > 1) {
        ctx.strokeStyle = "rgba(76,175,80,0.7)";
        ctx.lineWidth = 6;
        ctx.lineCap = "round";
        ctx.lineJoin = "round";

        ctx.beginPath();
        for (let i = 0; i < highlightPath.length; i++) {
            const cell = highlightPath[i];
            const cx = cell.x * cellSize + cellSize / 2;
            const cy = cell.y * cellSize + cellSize / 2;

            if (i === 0) ctx.moveTo(cx, cy);
            else ctx.lineTo(cx, cy);
        }
        ctx.stroke();
    }

    if (wrongPath && wrongPath.length > 1) {
        ctx.strokeStyle = "rgba(244,67,54,0.7)";
        ctx.lineWidth = 6;
        ctx.lineCap = "round";
        ctx.lineJoin = "round";

        ctx.beginPath();
        for (let i = 0; i < wrongPath.length; i++) {
            const cell = wrongPath[i];
            const cx = cell.x * cellSize + cellSize / 2;
            const cy = cell.y * cellSize + cellSize / 2;

            if (i === 0) ctx.moveTo(cx, cy);
            else ctx.lineTo(cx, cy);
        }
        ctx.stroke();
    }

    ctx.fillStyle = "#d0d0d0";
    const markerSize = cellSize * 0.15;
    grid.forEach(cell => {
        const cx = cell.x * cellSize + cellSize / 2;
        const cy = cell.y * cellSize + cellSize / 2;
        ctx.fillRect(cx - markerSize / 2, cy - markerSize / 2, markerSize, markerSize);
    });

    ctx.font = `${Math.floor(cellSize * 0.7)}px Arial`;
    ctx.textAlign = "center";
    ctx.textBaseline = "middle";

    ctx.fillStyle = "#87a8f2";
    ctx.fillRect(start.x * cellSize, start.y * cellSize, cellSize, cellSize);
    ctx.fillStyle = "#000000";
    ctx.fillText("A", start.x * cellSize + cellSize / 2, start.y * cellSize + cellSize / 2);

    ctx.fillStyle = "#f28b82";
    ctx.fillRect(end.x * cellSize, end.y * cellSize, cellSize, cellSize);
    ctx.fillStyle = "#000000";
    ctx.fillText("B", end.x * cellSize + cellSize / 2, end.y * cellSize + cellSize / 2);

    if (currentPosition) {
        ctx.strokeStyle = "#000000";
        ctx.lineWidth = 3;
        ctx.beginPath();
        ctx.arc(currentPosition.x * cellSize + cellSize / 2, currentPosition.y * cellSize + cellSize / 2, cellSize * 0.4, 0, 2 * Math.PI);
        ctx.fillStyle = "rgba(0, 0, 0, 0.4)";
        ctx.fill();
        ctx.stroke();
    }

    ctx.strokeStyle = "#000";
    ctx.lineWidth = 2;

    grid.forEach(cell => {
        const x = cell.x * cellSize;
        const y = cell.y * cellSize;
        const w = cell.walls;

        if (w.top) {
            ctx.beginPath(); ctx.moveTo(x, y); ctx.lineTo(x + cellSize, y); ctx.stroke();
        }
        if (w.right) {
            ctx.beginPath(); ctx.moveTo(x + cellSize, y); ctx.lineTo(x + cellSize, y + cellSize); ctx.stroke();
        }
        if (w.bottom) {
            ctx.beginPath(); ctx.moveTo(x, y + cellSize); ctx.lineTo(x + cellSize, y + cellSize); ctx.stroke();
        }
        if (w.left) {
            ctx.beginPath(); ctx.moveTo(x, y); ctx.lineTo(x, y + cellSize); ctx.stroke();
        }
    });

    return {
        image: canvas.createPNGStream(),
        solution: solutionCode,
        solutionPath,
        grid,
        cols
    };
}

function trans(emos) {
    return emos
        .replace(/‚¨ÜÔ∏è/gi, "u")
        .replace(/‚û°Ô∏è/gi, "r")
        .replace(/‚¨áÔ∏è/gi, "d")
        .replace(/‚¨ÖÔ∏è/gi, "l");
}

function getPathFromCode(code, grid, cols, startCell) {
    const rows = grid.length / cols;
    const path = [startCell];
    let current = startCell;

    function index(x, y) {
        if (x < 0 || y < 0 || x >= cols || y >= rows) return -1;
        return x + y * cols;
    }

    for (const move of code) {
        let nx = current.x;
        let ny = current.y;

        if (move === "u") ny--;
        if (move === "d") ny++;
        if (move === "l") nx--;
        if (move === "r") nx++;

        const idx = index(nx, ny);

        if (idx === -1) return path;

        const next = grid[idx];
        const valid =
            (move === "u" && !current.walls.top) ||
            (move === "d" && !current.walls.bottom) ||
            (move === "l" && !current.walls.left) ||
            (move === "r" && !current.walls.right);

        if (!valid) return path;

        path.push(next);
        current = next;
    }

    return path;
}

function isPartialSolutionCorrect(userPath, solutionPath, fullCode) {
    if (userPath.length !== fullCode.length + 1) return false;

    for (let i = 0; i < userPath.length; i++) {
        if (!solutionPath[i] || userPath[i].x !== solutionPath[i].x || userPath[i].y !== solutionPath[i].y) {
            return false;
        }
    }
    return true;
}

exports.onStart = async ({ args, message, event, commandName }) => {
    let difficultyLevel = 8;
    let difficultyMessage = "Medium";

    if (args.length > 0) {
        const input = args[0].toLowerCase();
        const inputNumber = parseInt(input);

        if (!isNaN(inputNumber)) {
            difficultyLevel = Math.max(1, Math.min(10, inputNumber));
            difficultyMessage = `Level ${difficultyLevel}`;
        } else if (input === 'easy') {
            difficultyLevel = 4;
            difficultyMessage = "Easy (Level 4)";
        } else if (input === 'medium') {
            difficultyLevel = 8;
            difficultyMessage = "Medium (Level 8)";
        } else if (input === 'hard') {
            difficultyLevel = 13;
            difficultyMessage = "Hard (Level 13)";
        }
    }

    const data = generateMazeImage(difficultyLevel);

    const imagePath = path.join(__dirname, global.utils.randomString(4) + ".png");

    const writeStream = fs.createWriteStream(imagePath);
    data.image.pipe(writeStream);

    await new Promise((resolve) => writeStream.on('finish', resolve));

    const reply = await message.reply({
        body: `üß© Solve the maze! Difficulty: ${difficultyMessage}\n\n‚Ä¢ Send your path in one message (e.g., ‚û°Ô∏è‚û°Ô∏è‚¨áÔ∏è...)\n‚Ä¢ A is the start, B is the end.\n‚Ä¢ You have 3 attempts for wrong answers.`,
        attachment: fs.createReadStream(imagePath)
    });

    fs.unlinkSync(imagePath);

    global.GoatBot.onReply.set(reply.messageID, {
        commandName,
        au: event.senderID,
        solution: data.solution,
        solutionPath: data.solutionPath,
        grid: data.grid,
        cols: data.cols,
        attempts: 0,
        currentProgress: "",
        currentPosition: data.grid[0],
        // Store the final calculated difficulty level for reward scaling
        finalDifficulty: difficultyLevel 
    });
};

exports.onReply = async ({ message, event, Reply, usersData }) => {
    const { au, solution, solutionPath, grid, cols, currentProgress, finalDifficulty } = Reply;
    if (event.senderID !== au) return;

    const userEmoji = event.body.trim();
    const userCode = trans(userEmoji);

    if (!/^[urdl‚¨ÜÔ∏è‚û°Ô∏è‚¨áÔ∏è‚¨ÖÔ∏è]+$/i.test(userEmoji)) {
        return message.reply(`Please only use valid move emojis (‚¨ÜÔ∏è ‚û°Ô∏è ‚¨áÔ∏è ‚¨ÖÔ∏è) or their corresponding letters (u, r, d, l) in one sequence.`);
    }
    if (!/^[urdl]+$/i.test(userCode)) {
        return message.reply(`Please only use valid move emojis (‚¨ÜÔ∏è ‚û°Ô∏è ‚¨áÔ∏è ‚¨ÖÔ∏è) or their corresponding letters (u, r, d, l) in one sequence.`);
    }

    const fullCode = currentProgress + userCode;
    const startCell = grid[0];

    const userPath = getPathFromCode(fullCode, grid, cols, startCell);

    const isCorrectContinuation = isPartialSolutionCorrect(userPath, solutionPath, fullCode);

    if (isCorrectContinuation && userPath.length === solutionPath.length) {
        // Dynamic Reward Calculation: Base 20000 / 8 * finalDifficulty
        // Minimum reward is set to 2500 (1 * 2500)
        const baseCoinPerLevel = 2500; 
        const rewardAmount = Math.max(2500, Math.floor(baseCoinPerLevel * (finalDifficulty || 8))); 

        try {
            const userData = await usersData.get(event.senderID);
            await usersData.set(event.senderID, {
                money: userData.money + rewardAmount,
                exp: userData.exp,
                data: userData.data
            });

            const data = generateMazeImage(15, grid, cols, solutionPath, null);

            const imagePath = path.join(__dirname, global.utils.randomString(4) + ".png");
            const writeStream = fs.createWriteStream(imagePath);
            data.image.pipe(writeStream);
            await new Promise((resolve) => writeStream.on('finish', resolve));

            await message.reply({
                body: `üéâ Correct! You solved the maze and earned $${rewardAmount.toLocaleString()}! The solution is shown in green.`,
                attachment: fs.createReadStream(imagePath)
            });
            fs.unlinkSync(imagePath);
            global.GoatBot.onReply.delete(event.messageID);
        } catch (e) {
             message.reply(`üéâ You solved the maze! (Error crediting money: ${e.message})`);
             global.GoatBot.onReply.delete(event.messageID);
        }
        return;
    }

    if (isCorrectContinuation) {
        // CORRECT CONTINUATION
        const currentCell = userPath[userPath.length - 1];
        Reply.currentProgress = fullCode;
        Reply.currentPosition = currentCell;
        Reply.attempts = 0;

        const data = generateMazeImage(5, grid, cols, null, null, currentCell, userPath);

        const imagePath = path.join(__dirname, global.utils.randomString(4) + ".png");
        const writeStream = fs.createWriteStream(imagePath);
        data.image.pipe(writeStream);
        await new Promise((resolve) => writeStream.on('finish', resolve));

        global.GoatBot.onReply.delete(event.messageID);

        const newReply = await message.reply({
            body: `‚úÖ Correct path! Continue from your position.\n\nüìç Progress: ${fullCode.length}/${solution.length} moves\nüéØ Keep going to reach point B!`,
            attachment: fs.createReadStream(imagePath)
        });
        fs.unlinkSync(imagePath);

        global.GoatBot.onReply.set(newReply.messageID, Reply);
        return;
    }

    // WRONG PATH/MOVE
    Reply.attempts++;
    if (Reply.attempts >= 3) {
        // GAME OVER
        const data = generateMazeImage(15, grid, cols, solutionPath, userPath);

        const imagePath = path.join(__dirname, global.utils.randomString(4) + ".png");
        const writeStream = fs.createWriteStream(imagePath);
        data.image.pipe(writeStream);
        await new Promise((resolve) => writeStream.on('finish', resolve));

        await message.reply({
            body: `‚ùå Game over! You ran out of attempts.\n\n‚úÖ Correct solution shown in green\n‚ùå Your incorrect path shown in red`,
            attachment: fs.createReadStream(imagePath)
        });
        fs.unlinkSync(imagePath);
        global.GoatBot.onReply.delete(event.messageID);
    } else {
        // WRONG MOVE, ATTEMPTS REMAINING
        await message.reply(`‚ùå Wrong path or move! Try again from your last checkpoint.\n\nüîÑ Attempts remaining: ${3 - Reply.attempts}`);
    }
};

scripts/cmds/dev.js.content:
const { config } = global.GoatBot;
const { writeFileSync } = require("fs-extra");

module.exports = {
        config: {
                name: "dev",
                version: "1.0",
                author: "NeoKEX",
                countDown: 5,
                role: 4,
                description: {
                        vi: "Th√™m, x√≥a, s·ª≠a quy·ªÅn developer",
                        en: "Add, remove, edit developer role"
                },
                category: "owner",
                guide: {
                        vi: '   {pn} [add | -a] <uid | @tag>: Th√™m quy·ªÅn developer cho ng∆∞·ªùi d√πng'
                                + '\n     {pn} [remove | -r] <uid | @tag>: X√≥a quy·ªÅn developer c·ªßa ng∆∞·ªùi d√πng'
                                + '\n     {pn} [list | -l]: Li·ªát k√™ danh s√°ch developers',
                        en: '   {pn} [add | -a] <uid | @tag>: Add developer role for user'
                                + '\n     {pn} [remove | -r] <uid | @tag>: Remove developer role of user'
                                + '\n     {pn} [list | -l]: List all developers'
                }
        },

        langs: {
                vi: {
                        added: "‚úì | ƒê√£ th√™m quy·ªÅn developer cho %1 ng∆∞·ªùi d√πng:\n%2",
                        alreadyDev: "\n‚ö† | %1 ng∆∞·ªùi d√πng ƒë√£ c√≥ quy·ªÅn developer t·ª´ tr∆∞·ªõc r·ªìi:\n%2",
                        missingIdAdd: "‚ö† | Vui l√≤ng nh·∫≠p ID ho·∫∑c tag ng∆∞·ªùi d√πng mu·ªën th√™m quy·ªÅn developer",
                        removed: "‚úì | ƒê√£ x√≥a quy·ªÅn developer c·ªßa %1 ng∆∞·ªùi d√πng:\n%2",
                        notDev: "‚ö† | %1 ng∆∞·ªùi d√πng kh√¥ng c√≥ quy·ªÅn developer:\n%2",
                        missingIdRemove: "‚ö† | Vui l√≤ng nh·∫≠p ID ho·∫∑c tag ng∆∞·ªùi d√πng mu·ªën x√≥a quy·ªÅn developer",
                        listDev: "‚öô | Danh s√°ch developers:\n%1"
                },
                en: {
                        added: "‚úì | Added developer role for %1 users:\n%2",
                        alreadyDev: "\n‚ö† | %1 users already have developer role:\n%2",
                        missingIdAdd: "‚ö† | Please enter ID or tag user to add developer role",
                        removed: "‚úì | Removed developer role of %1 users:\n%2",
                        notDev: "‚ö† | %1 users don't have developer role:\n%2",
                        missingIdRemove: "‚ö† | Please enter ID or tag user to remove developer role",
                        listDev: "‚öô | List of developers:\n%1"
                }
        },

        onStart: async function ({ message, args, usersData, event, getLang }) {
                if (!config.devUsers)
                        config.devUsers = [];

                switch (args[0]) {
                        case "add":
                        case "-a": {
                                if (args[1]) {
                                        let uids = [];
                                        if (Object.keys(event.mentions).length > 0)
                                                uids = Object.keys(event.mentions);
                                        else if (event.messageReply)
                                                uids.push(event.messageReply.senderID);
                                        else
                                                uids = args.filter(arg => !isNaN(arg));
                                        const notDevIds = [];
                                        const devIds = [];
                                        for (const uid of uids) {
                                                if (config.devUsers.includes(uid))
                                                        devIds.push(uid);
                                                else
                                                        notDevIds.push(uid);
                                        }

                                        config.devUsers.push(...notDevIds);
                                        const getNames = await Promise.all(uids.map(uid => usersData.getName(uid).then(name => ({ uid, name }))));
                                        writeFileSync(global.client.dirConfig, JSON.stringify(config, null, 2));
                                        return message.reply(
                                                (notDevIds.length > 0 ? getLang("added", notDevIds.length, getNames.map(({ uid, name }) => `‚Ä¢ ${name} (${uid})`).join("\n")) : "")
                                                + (devIds.length > 0 ? getLang("alreadyDev", devIds.length, devIds.map(uid => `‚Ä¢ ${uid}`).join("\n")) : "")
                                        );
                                }
                                else
                                        return message.reply(getLang("missingIdAdd"));
                        }
                        case "remove":
                        case "-r": {
                                if (args[1]) {
                                        let uids = [];
                                        if (Object.keys(event.mentions).length > 0)
                                                uids = Object.keys(event.mentions);
                                        else
                                                uids = args.filter(arg => !isNaN(arg));
                                        const notDevIds = [];
                                        const devIds = [];
                                        for (const uid of uids) {
                                                if (config.devUsers.includes(uid))
                                                        devIds.push(uid);
                                                else
                                                        notDevIds.push(uid);
                                        }
                                        for (const uid of devIds)
                                                config.devUsers.splice(config.devUsers.indexOf(uid), 1);
                                        const getNames = await Promise.all(devIds.map(uid => usersData.getName(uid).then(name => ({ uid, name }))));
                                        writeFileSync(global.client.dirConfig, JSON.stringify(config, null, 2));
                                        return message.reply(
                                                (devIds.length > 0 ? getLang("removed", devIds.length, getNames.map(({ uid, name }) => `‚Ä¢ ${name} (${uid})`).join("\n")) : "")
                                                + (notDevIds.length > 0 ? getLang("notDev", notDevIds.length, notDevIds.map(uid => `‚Ä¢ ${uid}`).join("\n")) : "")
                                        );
                                }
                                else
                                        return message.reply(getLang("missingIdRemove"));
                        }
                        case "list":
                        case "-l": {
                                const getNames = await Promise.all(config.devUsers.map(uid => usersData.getName(uid).then(name => ({ uid, name }))));
                                return message.reply(getLang("listDev", getNames.map(({ uid, name }) => `‚Ä¢ ${name} (${uid})`).join("\n")));
                        }
                        default:
                                return message.SyntaxError();
                }
        }
};

bot/login/socketIO.js.content:
// This module will be called if enabled in the config (severUptime.socket.enable = true)
/**
 * @example for connect to socket.io
 * view file ./connectSocketIO.example.js
 */
const { Server } = require("socket.io");
const { log, getText } = global.utils;
const { config } = global.GoatBot;

module.exports = async (server) => {
	const { channelName, verifyToken } = config.serverUptime.socket;
	let io;

	try {
		if (!channelName)
			throw ('"channelName" is not defined in config');
		if (!verifyToken)
			throw ('"verifyToken" is not defined in config');
		io = new Server(server);
		log.info("SOCKET IO", getText("socketIO", "connected"));
	}
	catch (err) {
		return log.err("SOCKET IO", getText("socketIO", "error"), err);
	}

	io.on("connection", (socket) => {
		if (socket.handshake.query.verifyToken != verifyToken) {
			io.to(socket.id).emit(channelName, {
				status: "error",
				message: "Token is invalid"
			});
			socket.disconnect();
			return;
		}
		log.info("SOCKET IO", `New client connected to socket: ${socket.id}`);
		io.to(socket.id).emit(channelName, {
			status: "success",
			message: "Connected to server successfully"
		});
		socket.on("disconnect", () => {
			log.info("SOCKET IO", `Client disconnected from socket: ${socket.id}`);
		});
	});
};

