			missingCommandNameToDelete: "‚ö† Vui l√≤ng nh·∫≠p t√™n l·ªánh b·∫°n mu·ªën x√≥a kh·ªèi danh s√°ch b·ªè qua",
			commandNotFound: "‚úó Kh√¥ng t√¨m th·∫•y l·ªánh \"%1\" trong danh s√°ch l·ªánh c·ªßa bot",
			commandAlreadyInList: "‚úó L·ªánh \"%1\" ƒë√£ c√≥ trong danh s√°ch b·ªè qua",
			commandAdded: "‚úì ƒê√£ th√™m l·ªánh \"%1\" v√†o danh s√°ch b·ªè qua",
			commandNotInList: "‚úó L·ªánh \"%1\" kh√¥ng c√≥ trong danh s√°ch b·ªè qua",
			commandDeleted: "‚úì ƒê√£ x√≥a l·ªánh \"%1\" kh·ªèi danh s√°ch b·ªè qua",
			ignoreList: "‚â° Danh s√°ch l·ªánh b·ªè qua trong nh√≥m b·∫°n:\n%1"
		},
		en: {
			missingCommandNameToAdd: "‚ö† Please enter the command name you want to add to the ignore list",
			missingCommandNameToDelete: "‚ö† Please enter the command name you want to delete from the ignore list",
			commandNotFound: "‚úó Command \"%1\" not found in bot's command list",
			commandAlreadyInList: "‚úó Command \"%1\" already in ignore list",
			commandAdded: "‚úì Added command \"%1\" to ignore list",
			commandNotInList: "‚úó Command \"%1\" not in ignore list",
			commandDeleted: "‚úì Removed command \"%1\" from ignore list",
			ignoreList: "‚â° Ignore list in your group:\n%1"
		}
	},

	onStart: async function ({ args, message, threadsData, getLang, event }) {
		const ignoreList = await threadsData.get(event.threadID, "data.ignoreCommanToOnlyAdminBox", []);
		switch (args[0]) {
			case "add": {
				if (!args[1])
					return message.reply(getLang("missingCommandNameToAdd"));
				const commandName = args[1].toLowerCase();
				const command = global.GoatBot.commands.get(commandName);
				if (!command)
					return message.reply(getLang("commandNotFound", commandName));
				if (ignoreList.includes(commandName))
					return message.reply(getLang("commandAlreadyInList", commandName));
				ignoreList.push(commandName);
				await threadsData.set(event.threadID, ignoreList, "data.ignoreCommanToOnlyAdminBox");
				return message.reply(getLang("commandAdded", commandName));
			}
			case "del":
			case "delete":
			case "remove":
			case "rm":
			case "-d": {
				if (!args[1])
					return message.reply(getLang("missingCommandNameToDelete"));
				const commandName = args[1].toLowerCase();
				const command = global.GoatBot.commands.get(commandName);
				if (!command)
					return message.reply(getLang("commandNotFound", commandName));

				if (!ignoreList.includes(commandName))
					return message.reply(getLang("commandNotInList", commandName));
				ignoreList.splice(ignoreList.indexOf(commandName), 1);
				await threadsData.set(event.threadID, ignoreList, "data.ignoreCommanToOnlyAdminBox");
				return message.reply(getLang("commandDeleted", commandName));
			}
			case "list": {
				return message.reply(getLang("ignoreList", ignoreList.join(", ")));
			}
			default: {
				return message.SyntaxError();
			}
		}
	}
};

