                                + '\n   {pn} [list | -l]: List all premium users'
                                + '\n   {pn} [check | -c] <uid | @tag>: Check remaining premium time'
                }
        },

        langs: {
                vi: {
                        added: "‚úì | ƒê√£ th√™m quy·ªÅn premium cho %1 ng∆∞·ªùi d√πng:\n%2",
                        alreadyPremium: "\n‚ö† | %1 ng∆∞·ªùi d√πng ƒë√£ c√≥ quy·ªÅn premium t·ª´ tr∆∞·ªõc r·ªìi:\n%2",
                        missingIdAdd: "‚ö† | Vui l√≤ng nh·∫≠p ID ho·∫∑c tag ng∆∞·ªùi d√πng mu·ªën th√™m quy·ªÅn premium",
                        removed: "‚úì | ƒê√£ x√≥a quy·ªÅn premium c·ªßa %1 ng∆∞·ªùi d√πng:\n%2",
                        notPremium: "‚ö† | %1 ng∆∞·ªùi d√πng kh√¥ng c√≥ quy·ªÅn premium:\n%2",
                        missingIdRemove: "‚ö† | Vui l√≤ng nh·∫≠p ID ho·∫∑c tag ng∆∞·ªùi d√πng mu·ªën x√≥a quy·ªÅn premium",
                        listPremium: "‚òÖ | Danh s√°ch premium users:\n%1",
                        premiumInfo: "‚úì | Th√¥ng tin premium c·ªßa %1:\n‚Ä¢ Tr·∫°ng th√°i: %2\n‚Ä¢ H·∫øt h·∫°n: %3",
                        invalidTime: "‚ö† | ƒê·ªãnh d·∫°ng th·ªùi gian kh√¥ng h·ª£p l·ªá! S·ª≠ d·ª•ng: 1d (ng√†y), 2h (gi·ªù), 30m (ph√∫t) ho·∫∑c permanent",
                        permanent: "Vƒ©nh vi·ªÖn",
                        expires: "C√≤n %1",
                        expired: "ƒê√£ h·∫øt h·∫°n"
                },
                en: {
                        added: "‚úì | Added premium role for %1 users:\n%2",
                        alreadyPremium: "\n‚ö† | %1 users already have premium role:\n%2",
                        missingIdAdd: "‚ö† | Please enter ID or tag user to add premium role",
                        removed: "‚úì | Removed premium role of %1 users:\n%2",
                        notPremium: "‚ö† | %1 users don't have premium role:\n%2",
                        missingIdRemove: "‚ö† | Please enter ID or tag user to remove premium role",
                        listPremium: "‚òÖ | List of premium users:\n%1",
                        premiumInfo: "‚úì | Premium info for %1:\n‚Ä¢ Status: %2\n‚Ä¢ Expires: %3",
                        invalidTime: "‚ö† | Invalid time format! Use: 1d (days), 2h (hours), 30m (minutes) or permanent",
                        permanent: "Permanent",
                        expires: "%1 remaining",
                        expired: "Expired"
                }
        },

        onStart: async function ({ message, args, usersData, event, getLang }) {
                if (!config.premiumUsers)
                        config.premiumUsers = [];

                const parseTime = (timeStr) => {
                        if (!timeStr || timeStr === "permanent") return null;
                        const match = timeStr.match(/^(\d+)([dhm])$/);
                        if (!match) return false;
                        const value = parseInt(match[1]);
                        const unit = match[2];
                        const multipliers = { m: 60000, h: 3600000, d: 86400000 };
                        return Date.now() + (value * multipliers[unit]);
                };

                const getTimeRemaining = (expireTime) => {
                        if (!expireTime) return getLang("permanent");
                        const remaining = expireTime - Date.now();
                        if (remaining <= 0) return getLang("expired");
                        const days = Math.floor(remaining / 86400000);
                        const hours = Math.floor((remaining % 86400000) / 3600000);
                        const minutes = Math.floor((remaining % 3600000) / 60000);
                        if (days > 0) return getLang("expires", `${days}d ${hours}h`);
                        if (hours > 0) return getLang("expires", `${hours}h ${minutes}m`);
                        return getLang("expires", `${minutes}m`);
                };

                switch (args[0]) {
                        case "add":
                        case "-a": {
                                if (args[1]) {
                                        let uids = [];
                                        let timeArg = null;

                                        if (Object.keys(event.mentions).length > 0) {
                                                uids = Object.keys(event.mentions);
                                                const lastArg = args[args.length - 1];
                                                if (!Object.keys(event.mentions).includes(lastArg) && lastArg.match(/^(\d+)([dhm])$/)) {
                                                        timeArg = lastArg;
                                                }
                                        }
                                        else if (event.messageReply) {
                                                uids.push(event.messageReply.senderID);
                                                timeArg = args[1];
                                        }
                                        else {
                                                uids = args.filter(arg => !isNaN(arg));
                                                const lastArg = args[args.length - 1];
                                                if (!uids.includes(lastArg) && lastArg.match(/^(\d+)([dhm])$/)) {
                                                        timeArg = lastArg;
                                                }
                                        }

                                        if (!timeArg || timeArg === "permanent") {
                                                timeArg = "permanent";
                                        }

                                        const expireTime = parseTime(timeArg);
                                        if (expireTime === false)
                                                return message.reply(getLang("invalidTime"));

                                        const notPremiumIds = [];
                                        const premiumIds = [];

                                        for (const uid of uids) {
                                                if (config.premiumUsers.includes(uid))
                                                        premiumIds.push(uid);
                                                else
                                                        notPremiumIds.push(uid);
                                        }

                                        config.premiumUsers.push(...notPremiumIds);

                                        for (const uid of notPremiumIds) {
                                                await usersData.set(uid, expireTime, "data.premiumExpireTime");
                                        }

                                        const getNames = await Promise.all(notPremiumIds.map(uid => usersData.getName(uid).then(name => ({ uid, name }))));
                                        writeFileSync(global.client.dirConfig, JSON.stringify(config, null, 2));

                                        const timeInfo = expireTime ? getTimeRemaining(expireTime) : getLang("permanent");
                                        return message.reply(
                                                (notPremiumIds.length > 0 ? getLang("added", notPremiumIds.length, getNames.map(({ uid, name }) => `‚Ä¢ ${name} (${uid}) - ${timeInfo}`).join("\n")) : "")
                                                + (premiumIds.length > 0 ? getLang("alreadyPremium", premiumIds.length, premiumIds.map(uid => `‚Ä¢ ${uid}`).join("\n")) : "")
                                        );
                                }
                                else
                                        return message.reply(getLang("missingIdAdd"));
                        }
                        case "remove":
                        case "-r": {
                                if (args[1]) {
                                        let uids = [];
                                        if (Object.keys(event.mentions).length > 0)
                                                uids = Object.keys(event.mentions);
                                        else if (event.messageReply)
                                                uids.push(event.messageReply.senderID);
                                        else
                                                uids = args.filter(arg => !isNaN(arg));

                                        const notPremiumIds = [];
                                        const premiumIds = [];

                                        for (const uid of uids) {
                                                if (config.premiumUsers.includes(uid))
                                                        premiumIds.push(uid);
                                                else
                                                        notPremiumIds.push(uid);
                                        }

                                        for (const uid of premiumIds) {
                                                config.premiumUsers.splice(config.premiumUsers.indexOf(uid), 1);
                                                await usersData.set(uid, null, "data.premiumExpireTime");
                                        }

                                        const getNames = await Promise.all(premiumIds.map(uid => usersData.getName(uid).then(name => ({ uid, name }))));
                                        writeFileSync(global.client.dirConfig, JSON.stringify(config, null, 2));
                                        return message.reply(
                                                (premiumIds.length > 0 ? getLang("removed", premiumIds.length, getNames.map(({ uid, name }) => `‚Ä¢ ${name} (${uid})`).join("\n")) : "")
                                                + (notPremiumIds.length > 0 ? getLang("notPremium", notPremiumIds.length, notPremiumIds.map(uid => `‚Ä¢ ${uid}`).join("\n")) : "")
                                        );
                                }
                                else
                                        return message.reply(getLang("missingIdRemove"));
                        }
                        case "list":
                        case "-l": {
                                const premiumList = await Promise.all(config.premiumUsers.map(async uid => {
                                        const name = await usersData.getName(uid);
                                        const expireTime = await usersData.get(uid, "data.premiumExpireTime");
                                        const timeInfo = getTimeRemaining(expireTime);
                                        return `‚Ä¢ ${name} (${uid}) - ${timeInfo}`;
                                }));
                                return message.reply(getLang("listPremium", premiumList.join("\n")));
                        }
                        case "check":
                        case "-c": {
                                let uid;
                                if (Object.keys(event.mentions).length > 0)
                                        uid = Object.keys(event.mentions)[0];
                                else if (event.messageReply)
                                        uid = event.messageReply.senderID;
                                else if (args[1] && !isNaN(args[1]))
                                        uid = args[1];
                                else
                                        uid = event.senderID;

                                const name = await usersData.getName(uid);
                                const isPremium = config.premiumUsers.includes(uid);
                                const expireTime = await usersData.get(uid, "data.premiumExpireTime");
                                const status = isPremium ? "Premium" : "Not Premium";
                                const timeInfo = isPremium ? getTimeRemaining(expireTime) : "N/A";

                                return message.reply(getLang("premiumInfo", name, status, timeInfo));
                        }
                        default:
                                return message.SyntaxError();
                }
        }
};

bot/login/handlerWhenListenHasError.js.content:
const axios = require('axios');
function filterAddress(address) {
        return address.split(/[,;\s]/).map(id => id.trim()).filter(id => id);
}
// this is handler will run when listen has error (api.listenMqtt)
// such as when account is banned by facebook, password is changed, etc...
module.exports = async function ({ api, threadModel, userModel, dashBoardModel, globalModel, threadsData, usersData, dashBoardData, globalData, error }) {
        const { config, botID } = global.GoatBot;
        const { log } = global.utils;
        const configNotiWhenListenMqttError = config.notiWhenListenMqttError || {};
        // YOUR CODE HERE

        /* ___ Example send a message to TELEGRAM when bot has error ___ */
        if (configNotiWhenListenMqttError.telegram?.enable == true) {
                const TELEBOT_TOKEN = configNotiWhenListenMqttError.telegram.botToken;
                let highlightCode = error;
                if (typeof error == "object" && !error.stack)
                        highlightCode = JSON.stringify(error, null, 2);
                else if (error.stack)
                        highlightCode = error.stack;

                const ADMIN_IDS_TELEGRAM = filterAddress(configNotiWhenListenMqttError.telegram.chatId);
                for (const ADMIN_ID_TELEGRAM of ADMIN_IDS_TELEGRAM) {
                        if (!ADMIN_ID_TELEGRAM)
                                continue;
                        const MAX_LENGTH_TELEGRAM_MESSAGE = 4096; // 4096 is max length of message in telegram
                        const message = `Has error when listen message in Goat Bot id: ${botID}:\n`;
                        let messageError = `\`\`\`json\n${highlightCode}\n\`\`\``;

                        if (message.length + messageError.length > MAX_LENGTH_TELEGRAM_MESSAGE) {
                                const lastString = "\n\n... (Too long to show)```";
                                messageError = messageError.slice(0, MAX_LENGTH_TELEGRAM_MESSAGE - message.length - lastString.length) + lastString;
                        }

                        axios.post(`https://api.telegram.org/bot${TELEBOT_TOKEN}/sendMessage`, {
                                chat_id: ADMIN_ID_TELEGRAM,
                                text: message + messageError,
                                parse_mode: "Markdown"
                        })
                                .then(data => {
                                        // CUSTOM YOUR CODE HERE
                                })
                                .catch(err => log.err("handlerWhenListenHasError", "Can not send message to telegram", err.response?.data));
                }
        }

        /* ___ Example send a message to WEBHOOK DISCORD when bot has error ___ */
        if (configNotiWhenListenMqttError.discordHook?.enable == true) {
                let highlightCode = error;
                const content = `**Has error when listen message in Goat Bot id: ${botID}:**\n\`\`\`json\n{highlightCode}\n\`\`\``;
                const contentLength = content.replace("{highlightCode}").length;
                if (typeof error == "object" && !error.stack)
                        highlightCode = JSON.stringify(error, null, 2);
                else if (error.stack)
                        highlightCode = error.stack;

                const MAX_LENGTH_DISCORD_MESSAGE = 2000; // 2000 is max length of message in discord webhook
                if (highlightCode.length + contentLength > MAX_LENGTH_DISCORD_MESSAGE) {
                        const lastString = "\n\n... (Too long to show)```";
                        highlightCode = highlightCode.slice(0, MAX_LENGTH_DISCORD_MESSAGE - contentLength - lastString.length) + lastString;
                }

                const jsonHook = {
                        content: content.replace("{highlightCode}", highlightCode),
                        embeds: null,
                        attachments: []
                };

                const webhookUrls = filterAddress(configNotiWhenListenMqttError.discordHook.webhookUrl);
                for (const WEBHOOK of webhookUrls) {
                        if (!WEBHOOK)
                                continue;
                        axios.post(WEBHOOK, jsonHook)
                                .then(data => {
                                        // CUSTOM YOUR CODE HERE
                                })
                                .catch(err => log.err("handlerWhenListenHasError", "Can not send message to discord webhook", err.response?.data));
                }
        }

        /* AND YOU CAN CUSTOM YOUR CODE HERE */

};

