class TaskQueue {
        constructor(callback) {
                this.queue = [];
                this.running = null;
                this.callback = callback;
        }
        push(task) {
                this.queue.push(task);
                if (this.queue.length == 1)
                        this.next();
        }
        next() {
                if (this.queue.length > 0) {
                        const task = this.queue[0];
                        this.running = task;
                        this.callback(task, async (err, result) => {
                                this.running = null;
                                this.queue.shift();
                                this.next();
                        });
                }
        }
        length() {
                return this.queue.length;
        }
}

