                        cantBanAdmin: "‚úó | B·∫°n kh√¥ng th·ªÉ c·∫•m qu·∫£n tr·ªã vi√™n!",
                        existedBan: "‚úó | Ng∆∞·ªùi n√†y ƒë√£ b·ªã spam ban!",
                        bannedSuccess: "‚úì | ƒê√£ spam ban %1 trong %2!",
                        needAdmin: "‚ö† | Bot c·∫ßn quy·ªÅn qu·∫£n tr·ªã vi√™n ƒë·ªÉ kick th√†nh vi√™n",
                        unbannedSuccess: "‚úì | ƒê√£ b·ªè spam ban %1!",
                        userNotBanned: "‚ö† | Ng∆∞·ªùi n√†y kh√¥ng b·ªã spam ban",
                        noData: "‚â° | Kh√¥ng c√≥ ai b·ªã spam ban",
                        listBanned: "‚â° | Danh s√°ch spam ban (trang %1/%2)",
                        content: "%1/ %2 (%3)\nL√Ω do: Spam\nH·∫øt h·∫°n: %4\n\n",
                        autobanEnabled: "‚úì | ƒê√£ b·∫≠t t·ª± ƒë·ªông ph√°t hi·ªán spam",
                        autobanDisabled: "‚úì | ƒê√£ t·∫Øt t·ª± ƒë·ªông ph√°t hi·ªán spam",
                        autobanStatus: "‚Ñπ | T·ª± ƒë·ªông ph√°t hi·ªán spam: %1\nC·∫•u h√¨nh: %2 tin nh·∫Øn trong %3 gi√¢y",
                        configSuccess: "‚úì | ƒê√£ c·∫•u h√¨nh: %1 tin nh·∫Øn trong %2 gi√¢y",
                        invalidConfig: "‚ö† | C·∫•u h√¨nh kh√¥ng h·ª£p l·ªá! S·ª≠ d·ª•ng: {pn} config [s·ªë tin nh·∫Øn] [th·ªùi gian]",
                        invalidTime: "‚ö† | Th·ªùi gian kh√¥ng h·ª£p l·ªá! S·ª≠ d·ª•ng: 1d, 2h, 30m",
                        noName: "Ng∆∞·ªùi d√πng facebook"
                },
                en: {
                        notFoundTarget: "‚ö† | Please tag user or enter uid or reply to message",
                        cantSelfBan: "‚ö† | You can't ban yourself!",
                        cantBanAdmin: "‚úó | You can't ban administrators!",
                        existedBan: "‚úó | This user is already spam banned!",
                        bannedSuccess: "‚úì | Spam banned %1 for %2!",
                        needAdmin: "‚ö† | Bot needs admin permission to kick members",
                        unbannedSuccess: "‚úì | Unbanned %1 from spam ban!",
                        userNotBanned: "‚ö† | This user is not spam banned",
                        noData: "‚â° | No spam banned users",
                        listBanned: "‚â° | Spam ban list (page %1/%2)",
                        content: "%1/ %2 (%3)\nReason: Spam\nExpires: %4\n\n",
                        autobanEnabled: "‚úì | Automatic spam detection enabled",
                        autobanDisabled: "‚úì | Automatic spam detection disabled",
                        autobanStatus: "‚Ñπ | Automatic spam detection: %1\nConfig: %2 messages in %3 seconds",
                        configSuccess: "‚úì | Configured: %1 messages in %2 seconds",
                        invalidConfig: "‚ö† | Invalid config! Use: {pn} config [message count] [time]",
                        invalidTime: "‚ö† | Invalid time! Use: 1d, 2h, 30m",
                        noName: "Facebook user"
                }
        },

        onStart: async function ({ message, event, args, threadsData, getLang, usersData, api }) {
                const { members, adminIDs } = await threadsData.get(event.threadID);
                const { senderID } = event;

                const parseTime = (timeStr) => {
                        if (!timeStr) return 7200000; // 2h default
                        const match = timeStr.match(/^(\d+)([dhms])$/);
                        if (!match) return false;
                        const value = parseInt(match[1]);
                        const unit = match[2];
                        const multipliers = { s: 1000, m: 60000, h: 3600000, d: 86400000 };
                        return value * multipliers[unit];
                };

                const formatDuration = (ms) => {
                        const days = Math.floor(ms / 86400000);
                        const hours = Math.floor((ms % 86400000) / 3600000);
                        const minutes = Math.floor((ms % 3600000) / 60000);
                        if (days > 0) return `${days}d ${hours}h`;
                        if (hours > 0) return `${hours}h ${minutes}m`;
                        return `${minutes}m`;
                };

                const spamBans = await threadsData.get(event.threadID, 'data.spamban', []);

                // Autoban toggle
                if (args[0] === 'autoban') {
                        const currentStatus = await threadsData.get(event.threadID, 'data.spamban_auto', false);
                        if (args[1] === 'on') {
                                await threadsData.set(event.threadID, true, 'data.spamban_auto');
                                return message.reply(getLang('autobanEnabled'));
                        }
                        else if (args[1] === 'off') {
                                await threadsData.set(event.threadID, false, 'data.spamban_auto');
                                return message.reply(getLang('autobanDisabled'));
                        }
                        else {
                                const config = await threadsData.get(event.threadID, 'data.spamban_config', { messages: 5, time: 10 });
                                return message.reply(getLang('autobanStatus', currentStatus ? 'ON' : 'OFF', config.messages, config.time));
                        }
                }

                // Config spam detection
                if (args[0] === 'config') {
                        const msgCount = parseInt(args[1]);
                        const timeMatch = args[2]?.match(/^(\d+)s$/);
                        if (!msgCount || !timeMatch) {
                                return message.reply(getLang('invalidConfig'));
                        }
                        const timeSeconds = parseInt(timeMatch[1]);
                        await threadsData.set(event.threadID, { messages: msgCount, time: timeSeconds }, 'data.spamban_config');
                        return message.reply(getLang('configSuccess', msgCount, timeSeconds));
                }

                // Unban
                if (args[0] === 'unban') {
                        let target;
                        if (!isNaN(args[1]))
                                target = args[1];
                        else if (Object.keys(event.mentions || {}).length)
                                target = Object.keys(event.mentions)[0];
                        else if (event.messageReply?.senderID)
                                target = event.messageReply.senderID;
                        else
                                return message.reply(getLang('notFoundTarget'));

                        const index = spamBans.findIndex(item => item.id == target);
                        if (index === -1)
                                return message.reply(getLang('userNotBanned'));

                        spamBans.splice(index, 1);
                        await threadsData.set(event.threadID, spamBans, 'data.spamban');
                        const userName = members[target]?.name || await usersData.getName(target) || getLang('noName');
                        return message.reply(getLang('unbannedSuccess', userName));
                }

                // List
                if (args[0] === 'list') {
                        if (!spamBans.length)
                                return message.reply(getLang('noData'));

                        const limit = 20;
                        const page = parseInt(args[1]) || 1;
                        const start = (page - 1) * limit;
                        const end = page * limit;
                        const data = spamBans.slice(start, end);
                        let msg = '';
                        let count = 0;

                        for (const user of data) {
                                count++;
                                const name = members[user.id]?.name || await usersData.getName(user.id) || getLang('noName');
                                const expiryTime = moment(user.expireTime).tz(global.GoatBot.config.timeZone).format('HH:mm:ss DD/MM/YYYY');
                                msg += getLang('content', start + count, name, user.id, expiryTime);
                        }
                        return message.reply(getLang('listBanned', page, Math.ceil(spamBans.length / limit)) + '\n\n' + msg);
                }

                // Ban user
                let target;
                let timeArg = args[args.length - 1];

                if (event.messageReply?.senderID) {
                        target = event.messageReply.senderID;
                }
                else if (Object.keys(event.mentions || {}).length) {
                        target = Object.keys(event.mentions)[0];
                }
                else if (!isNaN(args[0])) {
                        target = args[0];
                }
                else {
                        return message.reply(getLang('notFoundTarget'));
                }

                if (target == senderID)
                        return message.reply(getLang('cantSelfBan'));
                if (adminIDs.includes(target))
                        return message.reply(getLang('cantBanAdmin'));

                const banned = spamBans.find(item => item.id == target);
                if (banned)
                        return message.reply(getLang('existedBan'));

                const duration = parseTime(timeArg);
                if (duration === false)
                        return message.reply(getLang('invalidTime'));

                const name = members[target]?.name || await usersData.getName(target) || getLang('noName');
                const expireTime = Date.now() + duration;

                const data = {
                        id: target,
                        expireTime,
                        bannedBy: senderID,
                        bannedAt: Date.now()
                };

                spamBans.push(data);
                await threadsData.set(event.threadID, spamBans, 'data.spamban');

                message.reply(getLang('bannedSuccess', name, formatDuration(duration)), () => {
                        if (members.some(item => item.userID == target)) {
                                if (adminIDs.includes(api.getCurrentUserID())) {
                                        if (event.participantIDs.includes(target))
                                                api.removeUserFromGroup(target, event.threadID);
                                }
                                else {
                                        message.send(getLang('needAdmin'));
                                }
                        }
                });
        }
};

database/controller/dashBoardData.js.content:
const { existsSync, writeJsonSync, readJSONSync } = require("fs-extra");
const moment = require("moment-timezone");
const path = require("path");
const _ = require("lodash");
const { CustomError, TaskQueue, getType } = global.utils;

const optionsWriteJSON = {
	spaces: 2,
	EOL: "\n"
};

const taskQueue = new TaskQueue(function (task, callback) {
	if (getType(task) === "AsyncFunction") {
		task()
			.then(result => callback(null, result))
			.catch(err => callback(err));
	}
	else {
		try {
			const result = task();
			callback(null, result);
		}
		catch (err) {
			callback(err);
		}
	}
});

const { creatingDashBoardData } = global.client.database;

module.exports = async function (databaseType, dashBoardModel, fakeGraphql) {
	let Dashboard = [];
	const pathDashBoardData = path.join(__dirname, "..", "data/dashBoardData.json");

	switch (databaseType) {
		case "mongodb":
			// delete keys '_id' and '__v' in all dashboard data
			Dashboard = (await dashBoardModel.find({}).lean()).map(item => _.omit(item, ["_id", "__v"]));
			break;
		case "sqlite":
			Dashboard = (await dashBoardModel.findAll()).map(item => item.get({ plain: true }));
			break;
		case "json":
			if (!existsSync(pathDashBoardData))
				writeJsonSync(pathDashBoardData, [], optionsWriteJSON);
			Dashboard = readJSONSync(pathDashBoardData);
			break;
	}
	global.db.allDashBoardData = Dashboard;

	async function save(email, userData, mode, path) {
		try {
			const index = _.findIndex(global.db.allDashBoardData, { email });
			if (index === -1 && mode === "update") {
				throw new CustomError({
					name: "USER_NOT_FOUND",
					message: `Can't find user with email: ${email} in database`
				});
			}

			switch (mode) {
				case "create": {
					switch (databaseType) {
						case "mongodb":
						case "sqlite": {
							let dataCreated = await dashBoardModel.create(userData);
							dataCreated = databaseType == "mongodb" ?
								_.omit(dataCreated._doc, ["_id", "__v"]) :
								dataCreated.get({ plain: true });
							global.db.allDashBoardData.push(dataCreated);
							return _.cloneDeep(dataCreated);
						}
						case "json": {
							const timeCreation = moment.tz().format();
							userData.createdAt = timeCreation;
							userData.updatedAt = timeCreation;
							global.db.allDashBoardData.push(userData);
							writeJsonSync(pathDashBoardData, global.db.allDashBoardData, optionsWriteJSON);
							return _.cloneDeep(userData);
						}
					}
					break;
				}
				case "update": {
					const oldUserData = global.db.allDashBoardData[index];
					const dataWillChange = {};

					if (Array.isArray(path) && Array.isArray(userData)) {
						path.forEach((p, index) => {
							const key = p.split(".")[0];
							dataWillChange[key] = oldUserData[key];
							_.set(dataWillChange, p, userData[index]);
						});
					}
					else
						if (path && typeof path === "string" || Array.isArray(path)) {
							const key = Array.isArray(path) ? path[0] : path.split(".")[0];
							dataWillChange[key] = oldUserData[key];
							_.set(dataWillChange, path, userData);
						}
						else
							for (const key in userData)
								dataWillChange[key] = userData[key];

					switch (databaseType) {
						case "mongodb": {
							let dataUpdated = await dashBoardModel.findOneAndUpdate({ email }, dataWillChange, { returnDocument: 'after' });
							dataUpdated = _.omit(dataUpdated._doc, ["_id", "__v"]);
							global.db.allDashBoardData[index] = dataUpdated;
							return _.cloneDeep(dataUpdated);
						}
						case "sqlite": {
							const getData = await dashBoardModel.findOne({ where: { email } });
							const dataUpdated = (await getData.update(dataWillChange)).get({ plain: true });
							global.db.allDashBoardData[index] = dataUpdated;
							return _.cloneDeep(dataUpdated);
						}
						case "json": {
							dataWillChange.updatedAt = moment.tz().format();
							global.db.allDashBoardData[index] = {
								...oldUserData,
								...dataWillChange
							};
							writeJsonSync(pathDashBoardData, global.db.allDashBoardData, optionsWriteJSON);
							return _.cloneDeep(global.db.allDashBoardData[index]);
						}
					}
					break;
				}
				case "remove": {
					if (index != -1) {
						global.db.allDashBoardData.splice(index, 1);
						if (databaseType == "mongodb")
							await dashBoardModel.deleteOne({ email });
						else if (databaseType == "sqlite")
							await dashBoardModel.destroy({ where: { email } });
						else
							writeJsonSync(pathDashBoardData, global.db.allDashBoardData, optionsWriteJSON);
					}
					break;
				}
			}
			return null;
		}
		catch (err) {
			throw err;
		}
	}


	async function create(data) {
		if (typeof data != "object" || Array.isArray(data))
			throw new CustomError({
				name: "INVALID_DATA",
				message: `The first argument(data) must be an object, not a ${Array.isArray(data) ? "array" : typeof data}`
			});
		const email = data.email;
		const findInCreatingData = creatingDashBoardData.find(u => u.email == email);
		if (findInCreatingData)
			return findInCreatingData.promise;

		const queue = new Promise(async function (resolve, reject) {
			try {
				if (global.db.allDashBoardData.some(u => u.email == email)) {
					throw new CustomError({
						name: "USER_ALREADY_EXISTS",
						message: `User with email "${email}" already exists in the data`
					});
				}

				const userData = await save(email, data, "create");
				resolve(_.cloneDeep(userData));
			}
			catch (err) {
				reject(err);
			}
			creatingDashBoardData.splice(creatingDashBoardData.findIndex(u => u.email == email), 1);
		});
		creatingDashBoardData.push({
			email,
			promise: queue
		});
		return queue;
	}


	function getAll(path, defaultValue, query) {
		return new Promise((resolve, reject) => {
			taskQueue.push(async function () {
				try {
					let dataReturn = _.cloneDeep(global.db.allDashBoardData);

					if (query)
						if (typeof query !== "string")
							throw new CustomError({
								name: "INVALID_QUERY",
								message: `The third argument (query) must be a string, not a ${typeof query}`
							});
						else
							dataReturn = dataReturn.map(uData => fakeGraphql(query, uData));

					if (path)
						if (!["string", "object"].includes(typeof path))
							throw new CustomError({
								name: "INVALID_PATH",
								message: `The first argument (path) must be a string or an array, not a ${typeof path}`
							});
						else
							if (typeof path === "string")
								return resolve(_.cloneDeep(dataReturn.map(uData => _.get(uData, path, defaultValue))));
							else
								return resolve(_.cloneDeep(dataReturn.map(uData => _.times(path.length, i => _.get(uData, path[i], defaultValue[i])))));

					return resolve(_.cloneDeep(dataReturn));
				}
				catch (err) {
					reject(err);
				}
			});
		});
	}

	function get_(email, path, defaultValue, query) {
		return new Promise((resolve, reject) => {
			try {
				if (!email || typeof email != "string")
					throw new CustomError({
						name: "INVALID_EMAIL",
						message: `The first argument (email) must be a string, not a ${typeof email}`
					});
				let userData = global.db.allDashBoardData.find(u => u.email == email);
				if (!userData)
					return resolve(undefined);

				if (query)
					if (typeof query !== "string")
						throw new CustomError({
							name: "INVALID_QUERY",
							message: `The fourth argument (query) must be a string, not a ${typeof query}`
						});
					else userData = fakeGraphql(query, userData);

				if (path)
					if (!["string", "array"].includes(typeof path))
						throw new CustomError({
							name: "INVALID_PATH",
							message: `The second argument (path) must be a string or an array, not a ${typeof path}`
						});
					else
						if (typeof path === "string")
							return resolve(_.cloneDeep(_.get(userData, path, defaultValue)));
						else
							return resolve(_.cloneDeep(_.times(path.length, i => _.get(userData, path[i], defaultValue[i]))));
				return resolve(_.cloneDeep(userData));
			}
			catch (err) {
				reject(err);
			}
		});
	}

	function get(email, path, defaultValue, query) {
		return new Promise((resolve, reject) => {
			taskQueue.push(function () {
				get_(email, path, defaultValue, query)
					.then(resolve)
					.catch(reject);
			});
		});
	}

	async function set(email, updateData, path, query) {
		return new Promise((resolve, reject) => {
			taskQueue.push(async function () {
				try {
					if (!path && (typeof updateData != "object" || typeof updateData == "object" && Array.isArray(updateData)))
						throw new CustomError({
							name: "INVALID_UPDATE_DATA",
							message: `The second argument (updateData) must be an object or an array, not a ${typeof updateData}`
						});
					if (!global.db.allDashBoardData.some(u => u.email == email)) {
						throw new CustomError({
							name: "USER_NOT_FOUND",
							message: `User with email "${email}" does not exist in the data`
						});
					}
					const userData = await save(email, updateData, "update", path);
					if (query)
						if (typeof query !== "string")
							throw new CustomError({
								name: "INVALID_QUERY",
								message: `The fourth argument (query) must be a string, not a ${typeof query}`
							});
						else
							return resolve(_.cloneDeep(fakeGraphql(query, userData)));
					return resolve(_.cloneDeep(userData));
				}
				catch (err) {
					reject(err);
				}
			});
		});
	}

	async function deleteKey(email, path, query) {
		return new Promise((resolve, reject) => {
			taskQueue.push(async function () {
				try {
					if (typeof email != "string") {
						throw new CustomError({
							name: "INVALID_EMAIL",
							message: `The first argument (email) must be a string, not a ${typeof email}`
						});
					}
					if (!global.db.allDashBoardData.some(u => u.email == email)) {
						throw new CustomError({
							name: "USER_NOT_FOUND",
							message: `User with email "${email}" does not exist in the data`
						});
					}

					if (typeof path !== "string")
						throw new CustomError({
							name: "INVALID_PATH",
							message: `The second argument (path) must be a string, not a ${typeof path}`
						});
					const spitPath = path.split(".");
					if (spitPath.length == 1)
						throw new CustomError({
							name: "INVALID_PATH",
							message: `Can't delete key "${path}" because it's a root key`
						});
					const parent = spitPath.slice(0, spitPath.length - 1).join(".");
					const parentData = await get_(email, parent);
					if (!parentData)
						throw new CustomError({
							name: "INVALID_PATH",
							message: `Can't find key "${parent}" in user data`
						});

					_.unset(parentData, spitPath[spitPath.length - 1]);
					const setData = await save(email, parentData, "update", parent);
					if (query)
						if (typeof query !== "string")
							throw new CustomError({
								name: "INVALID_QUERY",
								message: `The fourth argument (query) must be a string, not a ${typeof query}`
							});
						else
							return resolve(_.cloneDeep(fakeGraphql(query, setData)));
					return resolve(_.cloneDeep(setData));
				}
				catch (err) {
					reject(err);
				}
			});
		});
	}

	async function remove(email) {
		return new Promise((resolve, reject) => {
			taskQueue.push(async function () {
				try {
					if (typeof threadID != "string") {
						throw new CustomError({
							name: "INVALID_EMAIL",
							message: `The first argument (email) must be a string, not a ${typeof email}`
						});
					}
					await save(email, { email }, "remove");
					return resolve(true);
				}
				catch (err) {
					reject(err);
				}
			});
		});
	}

	return {
		existsSync: function existsSync(email) {
			return global.db.allDashBoardData.some(u => u.email == email);
		},
		create,
		getAll,
		get,
		set,
		deleteKey,
		remove
	};
};

database/models/mongodb/user.js.content:
const mongoose = require("mongoose");
const { Schema } = mongoose;

const userModel = new Schema({
	userID: {
		type: String,
		unique: true
	},
	name: String,
	gender: Number,
	vanity: String,
	exp: {
		type: Number,
		default: 0
	},
	money: {
		type: Number,
		default: 0
	},
	banned: {
		type: Object,
		default: {}
	},
	settings: {
		type: Object,
		default: {}
	},
	data: {
		type: Object,
		default: {}
	}
}, {
	timestamps: true,
	minimize: false
});

module.exports = mongoose.model("users", userModel);

