const { drive, getStreamFromURL, getExtFromUrl, getTime } = global.utils;

module.exports = {
	config: {
		name: "setwelcome",
		aliases: ["setwc"],
		version: "1.7",
		author: "NTKhang",
		countDown: 5,
		role: 1,
		description: {
			vi: "Ch·ªânh s·ª≠a n·ªôi dung tin nh·∫Øn ch√†o m·ª´ng th√†nh vi√™n m·ªõi tham gia v√†o nh√≥m chat c·ªßa b·∫°n",
			en: "Edit welcome message content when new member join your group chat"
		},
		category: "custom",
		guide: {
			vi: {
				body: "   {pn} text [<n·ªôi dung> | reset]: ch·ªânh s·ª≠a n·ªôi dung vƒÉn b·∫£n ho·∫∑c reset v·ªÅ m·∫∑c ƒë·ªãnh, v·ªõi nh·ªØng shortcut c√≥ s·∫µn:"
					+ "\n  + {userName}: t√™n c·ªßa th√†nh vi√™n m·ªõi"
					+ "\n  + {userNameTag}: t√™n c·ªßa th√†nh vi√™n m·ªõi (tag)"
					+ "\n  + {boxName}:  t√™n c·ªßa nh√≥m chat"
					+ "\n  + {multiple}: b·∫°n || c√°c b·∫°n"
					+ "\n  + {session}:  bu·ªïi trong ng√†y"
					+ "\n\n   V√≠ d·ª•:"
					+ "\n    {pn} text Hello {userName}, welcome to {boxName}, ch√∫c {multiple} m·ªôt ng√†y m·ªõi vui v·∫ª"
					+ "\n"
					+ "\n   Reply (ph·∫£n h·ªìi) ho·∫∑c g·ª≠i k√®m m·ªôt tin nh·∫Øn c√≥ file v·ªõi n·ªôi dung {pn} file: ƒë·ªÉ th√™m t·ªáp ƒë√≠nh k√®m v√†o tin nh·∫Øn ch√†o m·ª´ng (·∫£nh, video, audio)"
					+ "\n\n   V√≠ d·ª•:"
					+ "\n    {pn} file reset: x√≥a g·ª≠i file",
				attachment: {
					[`${__dirname}/assets/guide/setwelcome/setwelcome_vi_1.png`]: "https://i.ibb.co/vd6bQrW/setwelcome-vi-1.png"
				}
			},
			en: {
				body: "   {pn} text [<content> | reset]: edit text content or reset to default, with some shortcuts:"
					+ "\n  + {userName}: new member name"
					+ "\n  + {userNameTag}: new member name (tag)"
					+ "\n  + {boxName}:  group chat name"
					+ "\n  + {multiple}: you || you guys"
					+ "\n  + {session}:  session in day"
					+ "\n\n   Example:"
					+ "\n    {pn} text Hello {userName}, welcome to {boxName}, have a nice day {multiple}"
					+ "\n"
					+ "\n   Reply (ph·∫£n h·ªìi) or send a message with file with content {pn} file: to add file attachments to welcome message (image, video, audio)"
					+ "\n\n   Example:"
					+ "\n    {pn} file reset: delete file attachments",
				attachment: {
					[`${__dirname}/assets/guide/setwelcome/setwelcome_en_1.png`]: "https://i.ibb.co/vsCz0ks/setwelcome-en-1.png"
				}
			}
		}
	},

	langs: {
		vi: {
			turnedOn: "ƒê√£ b·∫≠t ch·ª©c nƒÉng ch√†o m·ª´ng th√†nh vi√™n m·ªõi",
			turnedOff: "ƒê√£ t·∫Øt ch·ª©c nƒÉng ch√†o m·ª´ng th√†nh vi√™n m·ªõi",
			missingContent: "Vui l√πng nh·∫≠p n·ªôi dung tin nh·∫Øn",
			edited: "ƒê√£ ch·ªânh s·ª≠a n·ªôi dung tin nh·∫Øn ch√†o m·ª´ng c·ªßa nh√≥m b·∫°n th√†nh: %1",
			reseted: "ƒê√£ reset n·ªôi dung tin nh·∫Øn ch√†o m·ª´ng",
			noFile: "Kh√¥ng c√≥ t·ªáp ƒë√≠nh k√®m tin nh·∫Øn ch√†o m·ª´ng n√†o ƒë·ªÉ x√≥a",
			resetedFile: "ƒê√£ reset t·ªáp ƒë√≠nh k√®m th√†nh c√¥ng",
			missingFile: "H√£y ph·∫£n h·ªìi tin nh·∫Øn n√†y k√®m file ·∫£nh/video/audio",
			addedFile: "ƒê√£ th√™m %1 t·ªáp ƒë√≠nh k√®m v√†o tin nh·∫Øn ch√†o m·ª´ng c·ªßa nh√≥m b·∫°n"
		},
		en: {
			turnedOn: "Turned on welcome message",
			turnedOff: "Turned off welcome message",
			missingContent: "Please enter welcome message content",
			edited: "Edited welcome message content of your group to: %1",
			reseted: "Reseted welcome message content",
			noFile: "No file attachments to delete",
			resetedFile: "Reseted file attachments successfully",
			missingFile: "Please reply this message with image/video/audio file",
			addedFile: "Added %1 file attachments to your group welcome message"
		}
	},

	onStart: async function ({ args, threadsData, message, event, commandName, getLang }) {
		const { threadID, senderID, body } = event;
		const { data, settings } = await threadsData.get(threadID);

		switch (args[0]) {
			case "text": {
				if (!args[1])
					return message.reply(getLang("missingContent"));
				else if (args[1] == "reset")
					delete data.welcomeMessage;
				else
					data.welcomeMessage = body.slice(body.indexOf(args[0]) + args[0].length).trim();
				await threadsData.set(threadID, {
					data
				});
				message.reply(data.welcomeMessage ? getLang("edited", data.welcomeMessage) : getLang("reseted"));
				break;
			}
			case "file": {
				if (args[1] == "reset") {
					const { welcomeAttachment } = data;
					if (!welcomeAttachment)
						return message.reply(getLang("noFile"));
					try {
						await Promise.all(data.welcomeAttachment.map(fileId => drive.deleteFile(fileId)));
						delete data.welcomeAttachment;
					}
					catch (e) { }
					await threadsData.set(threadID, {
						data
					});
					message.reply(getLang("resetedFile"));
				}
				else if (event.attachments.length == 0 && (!event.messageReply || event.messageReply.attachments.length == 0))
					return message.reply(getLang("missingFile"), (err, info) => {
						global.GoatBot.onReply.set(info.messageID, {
							messageID: info.messageID,
							author: senderID,
							commandName
						});
					});
				else {
					saveChanges(message, event, threadID, senderID, threadsData, getLang);
				}
				break;
			}
			case "on":
			case "off": {
				settings.sendWelcomeMessage = args[0] == "on";
				await threadsData.set(threadID, { settings });
				message.reply(settings.sendWelcomeMessage ? getLang("turnedOn") : getLang("turnedOff"));
				break;
			}
			default:
				message.SyntaxError();
				break;
		}
	},

	onReply: async function ({ event, Reply, message, threadsData, getLang }) {
		const { threadID, senderID } = event;
		if (senderID != Reply.author)
			return;

		if (event.attachments.length == 0 && (!event.messageReply || event.messageReply.attachments.length == 0))
			return message.reply(getLang("missingFile"));
		saveChanges(message, event, threadID, senderID, threadsData, getLang);
	}
};

async function saveChanges(message, event, threadID, senderID, threadsData, getLang) {
	const { data } = await threadsData.get(threadID);
	const attachments = [...event.attachments, ...(event.messageReply?.attachments || [])].filter(item => ["photo", 'png', "animated_image", "video", "audio"].includes(item.type));
	if (!data.welcomeAttachment)
		data.welcomeAttachment = [];

	await Promise.all(attachments.map(async attachment => {
		const { url } = attachment;
		const ext = getExtFromUrl(url);
		const fileName = `${getTime()}.${ext}`;
		const infoFile = await drive.uploadFile(`setwelcome_${threadID}_${senderID}_${fileName}`, await getStreamFromURL(url));
		data.welcomeAttachment.push(infoFile.id);
	}));

	await threadsData.set(threadID, {
		data
	});
	message.reply(getLang("addedFile", attachments.length));
}

scripts/cmds/pin.js.content:
const axios = require("axios");
const { createCanvas, loadImage } = require('canvas');
const fs = require('fs-extra');
const path = require('path');
const { getStreamFromURL } = global.utils;

async function generatePinterestCanvas(imageObjects, query, page, totalPages) {
  const canvasWidth = 800;
  const canvasHeight = 1600;
  const canvas = createCanvas(canvasWidth, canvasHeight);
  const ctx = canvas.getContext('2d');

  ctx.fillStyle = '#1e1e1e';
  ctx.fillRect(0, 0, canvasWidth, canvasHeight);

  ctx.fillStyle = '#ffffff';
  ctx.font = '24px Arial';
  ctx.textAlign = 'left';
  ctx.fillText('üîç Pinterest Searcher', 20, 45);

  ctx.font = '16px Arial';
  ctx.fillStyle = '#b0b0b0';
  ctx.fillText(`Search results of "${query}", Showing up to ${imageObjects.length} images.`, 20, 75);

  const numColumns = 3;
  const padding = 15;
  const columnWidth = (canvasWidth - (padding * (numColumns + 1))) / numColumns;
  const columnHeights = Array(numColumns).fill(100);

  const loadedPairs = await Promise.all(
    imageObjects.map(obj =>
      loadImage(obj.url)
        .then(img => ({ img, originalIndex: obj.originalIndex, url: obj.url }))
        .catch(e => {
          console.error(`Failed to load image: ${obj.url}`, e && e.message);
          return null;
        })
    )
  );

  const successful = loadedPairs.filter(x => x !== null);

  if (successful.length === 0) {
    ctx.fillStyle = '#ff6666';
    ctx.font = '16px Arial';
    ctx.fillText(`No images could be loaded for this page.`, 20, 110);
    const outputPath = path.join(__dirname, 'cache', `pinterest_page_${Date.now()}.png`);
    await fs.ensureDir(path.dirname(outputPath));
    const buffer = canvas.toBuffer('image/png');
    fs.writeFileSync(outputPath, buffer);
    return { outputPath, displayedMap: [] };
  }

  let displayNumber = 0;
  const displayedMap = [];

  for (let i = 0; i < successful.length; i++) {
    const { img, originalIndex } = successful[i];

    const minHeight = Math.min(...columnHeights);
    const columnIndex = columnHeights.indexOf(minHeight);

    const x = padding + columnIndex * (columnWidth + padding);
    const y = minHeight + padding;

    const scale = columnWidth / img.width;
    const scaledHeight = img.height * scale;

    ctx.drawImage(img, x, y, columnWidth, scaledHeight);

    displayNumber += 1;
    displayedMap.push(originalIndex);

    ctx.fillStyle = 'rgba(0, 0, 0, 0.5)';
    ctx.fillRect(x, y, 50, 24);

    ctx.fillStyle = '#ffffff';
    ctx.font = 'bold 14px Arial';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText(`#${displayNumber}`, x + 25, y + 12);

    ctx.fillStyle = '#b0b0b0';
    ctx.font = '10px Arial';
    ctx.textAlign = 'right';
    ctx.textBaseline = 'alphabetic';
    ctx.fillText(`${img.width} x ${img.height}`, x + columnWidth - 6, y + scaledHeight - 6);

    columnHeights[columnIndex] += scaledHeight + padding;
  }

  ctx.fillStyle = '#ffffff';
  ctx.font = 'bold 18px Arial';
  ctx.textAlign = 'center';
  const footerY = Math.max(...columnHeights) + 40;
  ctx.fillText(`Anchestor - Page ${page}/${totalPages}`, canvasWidth / 2, footerY);

  const outputPath = path.join(__dirname, 'cache', `pinterest_page_${Date.now()}.png`);
  await fs.ensureDir(path.dirname(outputPath));
  const buffer = canvas.toBuffer('image/png');
  fs.writeFileSync(outputPath, buffer);

  return { outputPath, displayedMap };
}

module.exports = {
  config: {
    name: "pinterest",
    aliases: ["Pinterest", "pin"],
    version: "2.2",
    author: "Mahi--",
    countDown: 10,
    role: 0,
    shortDescription: "Search Pinterest for images",
    longDescription: "Search Pinterest for images, with canvas view for Browse.",
    category: "Image",
    guide: {
      en: "{pn} query [-count]\n" +
        "‚Ä¢ If count is used, it sends images directly.\n" +
        "‚Ä¢ If no count, it shows an interactive canvas.\n" +
        "‚Ä¢ Example: {pn} cute cat -5 (direct send)\n" +
        "‚Ä¢ Example: {pn} anime wallpaper (canvas view)"
    }
  },

  onStart: async function({ api, args, message, event }) {
    let processingMessage = null;
    try {
      let count = null;
      const countArg = args.find(arg => /^-\d+$/.test(arg));
      if (countArg) {
        count = parseInt(countArg.slice(1), 10);
        args = args.filter(arg => arg !== countArg);
      }
      const query = args.join(" ").trim();
      if (!query) {
        return message.reply("Please provide a search query.");
      }

      processingMessage = await message.reply("üîç Searching on Pinterest...");

      const res = await axios.get(`https://egret-driving-cattle.ngrok-free.app/api/pin?query=${encodeURIComponent(query)}&num=90`);
      const allImageUrls = res.data.results || [];

      if (allImageUrls.length === 0) {
        if (processingMessage) await message.unsend(processingMessage.messageID).catch(() => { });
        return message.reply(`No images found for "${query}".`);
      }

      if (count) {
        const urls = allImageUrls.slice(0, count);
        const streams = await Promise.all(urls.map(url => getStreamFromURL(url).catch(() => null)));
        const validStreams = streams.filter(s => s);

        if (processingMessage) await message.unsend(processingMessage.messageID).catch(() => { });

        return message.reply({
          body: `Here are ${validStreams.length} image(s) for "${query}":`,
          attachment: validStreams
        });

      } else {
        const imagesPerPage = 21;
        const totalPages = Math.ceil(allImageUrls.length / imagesPerPage);
        const startIndex = 0;
        const endIndex = Math.min(allImageUrls.length, imagesPerPage);
        const imagesForPage1 = allImageUrls.slice(startIndex, endIndex).map((url, idx) => ({
          url,
          originalIndex: startIndex + idx
        }));

        const { outputPath: canvasPath, displayedMap } = await generatePinterestCanvas(imagesForPage1, query, 1, totalPages);

        const sentMessage = await message.reply({
          body: `üñºÔ∏è Found ${allImageUrls.length} images for "${query}".\nReply with a number (shown on canvas) to get that image, or "next" for more.`,
          attachment: fs.createReadStream(canvasPath)
        });

        fs.unlink(canvasPath, (err) => {
          if (err) console.error(err);
        });

        global.GoatBot.onReply.set(sentMessage.messageID, {
          commandName: this.config.name,
          author: event.senderID,
          allImageUrls,
          query,
          imagesPerPage,
          currentPage: 1,
          totalPages,
          displayedMap,
          displayCount: Array.isArray(displayedMap) ? displayedMap.length : 0
        });

        if (processingMessage) await message.unsend(processingMessage.messageID).catch(() => { });
      }

    } catch (error) {
      console.error(error);
      if (processingMessage) {
        try { await message.unsend(processingMessage.messageID); } catch (e) { }
      }
      message.reply("An error occurred. The server or API might be down.");
    }
  },

  onReply: async function({ api, event, message, Reply }) {
    try {
      if (!Reply) return message.reply("Session expired. Please run the command again.");

      const { author, allImageUrls, query, imagesPerPage, currentPage, totalPages, displayedMap, displayCount } = Reply;
      if (event.senderID !== author) return;

      const input = (event.body || "").trim().toLowerCase();

      if (input === 'next') {
        if (currentPage >= totalPages) {
          return message.reply("This is the last page of results.");
        }
        const nextPage = currentPage + 1;
        const startIndex = (nextPage - 1) * imagesPerPage;
        const endIndex = Math.min(startIndex + imagesPerPage, allImageUrls.length);

        const imagesForNextPage = allImageUrls.slice(startIndex, endIndex).map((url, idx) => ({
          url,
          originalIndex: startIndex + idx
        }));

        const processingMessage = await message.reply(`Loading page ${nextPage}...`);
        const { outputPath: canvasPath, displayedMap: nextDisplayedMap } = await generatePinterestCanvas(imagesForNextPage, query, nextPage, totalPages);

        const sentMessage = await message.reply({
          body: `üñºÔ∏è Page ${nextPage}/${totalPages}.\nReply with a number (shown on canvas) to get that image, or "next" for more.`,
          attachment: fs.createReadStream(canvasPath)
        });
        fs.unlink(canvasPath, (err) => {
          if (err) console.error(err);
        });

        await message.unsend(processingMessage.messageID).catch(() => { });

        global.GoatBot.onReply.set(sentMessage.messageID, {
          commandName: this.config.name,
          author,
          allImageUrls,
          query,
          imagesPerPage,
          currentPage: nextPage,
          totalPages,
          displayedMap: nextDisplayedMap,
          displayCount: Array.isArray(nextDisplayedMap) ? nextDisplayedMap.length : 0
        });

      } else {
        const number = parseInt(input, 10);
        if (!isNaN(number) && number > 0) {
          if (!Array.isArray(displayedMap) || typeof displayCount !== 'number') {
            return message.reply("This page's images aren't available anymore. Please run the command again or type 'next'.");
          }

          if (number > displayCount) {
            return message.reply(`Invalid number. The current canvas shows only ${displayCount} image(s). Choose a number from 1 to ${displayCount}, or type "next" to load more images.`);
          }

          const originalIndex = displayedMap[number - 1];
          if (originalIndex == null || originalIndex < 0 || originalIndex >= allImageUrls.length) {
            return message.reply(`Could not find that image. Please try again or request a different number.`);
          }
          const imageUrl = allImageUrls[originalIndex];
          const stream = await getStreamFromURL(imageUrl).catch(() => null);
          if (!stream) return message.reply("Failed to fetch the requested image.");
          await message.reply({
            body: `Image #${number} for query "${query}":`,
            attachment: stream
          });
        } else {
          return message.reply(`Reply with a number (from the canvas) to get that image, or "next" for more pages.`);
        }
      }
    } catch (error) {
      console.error(error);
      message.reply("An error occurred while handling your reply.");
    }
  }
};

scripts/events/autoUpdateInfoThread.js.content:
module.exports = {
	config: {
		name: "autoUpdateThreadInfo",
		version: "1.4",
		author: "NTKhang",
		category: "events"
	},

	onStart: async ({ threadsData, event, api }) => {
		const types = ["log:subscribe", "log:unsubscribe", "log:thread-admins", "log:thread-name", "log:thread-image", "log:thread-icon", "log:thread-color", "log:user-nickname"];
		if (!types.includes(event.logMessageType))
			return;
		const { threadID, logMessageData, logMessageType } = event;
		const threadInfo = await threadsData.get(event.threadID);
		// eslint-disable-next-line prefer-const
		let { members, adminIDs } = threadInfo;
		switch (logMessageType) {
			case "log:subscribe":
				return async function () {
					const { addedParticipants } = event.logMessageData;
					const threadInfo_Fca = await api.getThreadInfo(threadID);
					threadsData.refreshInfo(threadID, threadInfo_Fca);

					for (const user of addedParticipants) {
						let oldData = members.find(member => member.userID === user.userFbId);
						const isOldMember = oldData ? true : false;
						oldData = oldData || {};
						const { userInfo, nicknames } = threadInfo_Fca;

						const newData = {
							userID: user.userFbId,
							name: user.fullName,
							gender: userInfo.find(u => u.id == user.userFbId)?.gender,
							nickname: nicknames[user.userFbId] || null,
							inGroup: true,
							count: oldData.count || 0
						};

						if (!isOldMember)
							members.push(newData);
						else {
							const index = members.findIndex(member => member.userID === user.userFbId);
							members[index] = newData;
						}
					}
					await threadsData.set(threadID, members, "members");
				};

			case "log:unsubscribe":
				return async function () {
					const oldData = members.find(member => member.userID === logMessageData.leftParticipantFbId);
					if (oldData) {
						oldData.inGroup = false;
						await threadsData.set(threadID, members, "members");
					}
				};

			case "log:thread-admins":
				return async function () {
					if (logMessageData.ADMIN_EVENT == "add_admin")
						adminIDs.push(logMessageData.TARGET_ID);
					else
						adminIDs = adminIDs.filter(uid => uid != logMessageData.TARGET_ID);
					adminIDs = [...new Set(adminIDs)];
					await threadsData.set(threadID, adminIDs, "adminIDs");
				};

			case "log:thread-name":
				return async function () {
					const threadName = logMessageData.name;
					await threadsData.set(threadID, threadName, "threadName");
				};
			case "log:thread-image":
				return async function () {
					await threadsData.set(threadID, logMessageData.url, "imageSrc");
				};
			case "log:thread-icon":
				return async function () {
					await threadsData.set(threadID, logMessageData.thread_icon, "emoji");
				};
			case "log:thread-color":
				return async function () {
					await threadsData.set(threadID, logMessageData.theme_id, "threadThemeID");
				};
			case "log:user-nickname":
				return async function () {
					const { participant_id, nickname } = logMessageData;
					const oldData = members.find(member => member.userID === participant_id);
					if (oldData) {
						oldData.nickname = nickname;
						await threadsData.set(threadID, members, "members");
					}
				};
			default:
				return null;
		}

	}
};

