                        rps_start: "üéÆ RPS BET üéÆ\n\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\nüíµ Bet: %1 coins\nüéØ Your Choice: %2\nüí∞ If Win: %3 coins\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n‚è≥ Bot choosing...",
                        rps_win: "üéâ YOU WON! üéâ\n\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\nü§ñ Bot chose: %1\nüë§ You chose: %2\nüí∞ Won: %3 coins!\n‚ú® New Balance: %4\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ",
                        rps_lose: "üò¢ YOU LOST üò¢\n\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\nü§ñ Bot chose: %1\nüë§ You chose: %2\nüí∏ Lost: %3 coins\nüòû New Balance: %4\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ",
                        rps_tie: "ü§ù IT'S A TIE! ü§ù\n\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\nü§ñ Bot chose: %1\nüë§ You chose: %1\nüí∞ Your coins back: %2\n‚ú® Balance: %3\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ",
                        insufficient: "‚ùå Not enough coins!\n\nüíµ You have: %1 coins\nüí∏ Need: %2 coins",
                        invalid_amount: "‚ùå Invalid bet amount!",
                        invalid_choice: "‚ùå Invalid choice! Use: rock, paper, scissors"
                }
        },

        onStart: async function ({ message, args, getLang, event, usersData }) {
                const userID = event.senderID;

                let userData = await usersData.get(userID, "data.economy");
                if (!userData) userData = { wallet: 0, bank: 0 };

                const gameType = args[0]?.toLowerCase() || "coinflip";
                let amount = 0;
                let choice = null;

                if (gameType === "rps") {
                        choice = args[1]?.toLowerCase();
                        amount = parseInt(args[2]);
                } else {
                        amount = parseInt(args[0]);
                }

                if (!amount || amount <= 0) return message.reply(getLang("invalid_amount"));
                if (userData.wallet < amount) return message.reply(getLang("insufficient", userData.wallet, amount));

                // COIN FLIP
                if (gameType !== "rps") {
                        await message.reply(getLang("coinflip_start", amount, amount * 2));
                        await new Promise(r => setTimeout(r, 2000));

                        const won = Math.random() < 0.5;
                        const result = won ? "Heads" : "Tails";

                        userData.wallet -= amount;
                        userData.level = userData.level || 1;
                        userData.exp = userData.exp || 0;

                        if (won) {
                                userData.wallet += amount * 2;
                                userData.exp += 15;
                        } else {
                                userData.exp += 5;
                        }

                        const expNeeded = 100 + (userData.level - 1) * 50;
                        if (userData.exp >= expNeeded) {
                                userData.level += 1;
                                userData.exp = 0;
                        }

                        await usersData.set(userID, userData, "data.economy");
                        const levelUp = userData.exp === 0 && won ? "\n\nüéâ **LEVEL UP!** You are now level " + userData.level + "!" : "";

                        if (won) {
                                return message.reply(getLang("coinflip_win", result, amount, userData.wallet) + levelUp);
                        } else {
                                return message.reply(getLang("coinflip_lose", result, amount, userData.wallet));
                        }
                }

                // ROCK PAPER SCISSORS BET
                const choices = ["rock", "paper", "scissors"];
                if (!choices.includes(choice)) return message.reply(getLang("invalid_choice"));

                await message.reply(getLang("rps_start", amount, choice, amount * 2));
                await new Promise(r => setTimeout(r, 1500));

                const botChoice = choices[Math.floor(Math.random() * 3)];
                userData.wallet -= amount;
                userData.level = userData.level || 1;
                userData.exp = userData.exp || 0;

                let won = false;
                if (choice === botChoice) {
                        userData.wallet += amount;
                        userData.exp += 10;
                } else if (
                        (choice === "rock" && botChoice === "scissors") ||
                        (choice === "paper" && botChoice === "rock") ||
                        (choice === "scissors" && botChoice === "paper")
                ) {
                        userData.wallet += amount * 2;
                        userData.exp += 15;
                        won = true;
                } else {
                        userData.exp += 5;
                }

                const expNeeded = 100 + (userData.level - 1) * 50;
                if (userData.exp >= expNeeded) {
                        userData.level += 1;
                        userData.exp = 0;
                }

                await usersData.set(userID, userData, "data.economy");

                const levelUp = userData.exp === 0 ? "\n\nüéâ **LEVEL UP!** You are now level " + userData.level + "!" : "";
                if (choice === botChoice) {
                        return message.reply(getLang("rps_tie", choice, amount, userData.wallet) + levelUp);
                } else if (won) {
                        return message.reply(getLang("rps_win", botChoice, choice, amount, userData.wallet) + levelUp);
                } else {
                        return message.reply(getLang("rps_lose", botChoice, choice, amount, userData.wallet));
                }
        }
};

