                vi: {
                        invalidChoice: "L·ª±a ch·ªçn kh√¥ng h·ª£p l·ªá! S·ª≠ d·ª•ng: rock, paper, ho·∫∑c scissors",
                        result: "üéÆ **ROCK PAPER SCISSORS**\nB·∫°n ch·ªçn: %1\nBot ch·ªçn: %2\n%3",
                        win: "üéâ B·∫°n th·∫Øng!",
                        lose: "üò¢ B·∫°n thua!",
                        tie: "ü§ù H√≤a!"
                },
                en: {
                        invalidChoice: "Invalid choice! Use: rock, paper, or scissors",
                        result: "üéÆ **ROCK PAPER SCISSORS**\nYou chose: %1\nBot chose: %2\n%3",
                        win: "üéâ You win!",
                        lose: "üò¢ You lose!",
                        tie: "ü§ù Draw!"
                }
        },

        onStart: async function ({ message, args, getLang, event, usersData }) {
                const choices = ["rock", "paper", "scissors"];
                const userChoice = args[0]?.toLowerCase();

                if (!choices.includes(userChoice)) {
                        return message.reply(getLang("invalidChoice"));
                }

                const botChoice = choices[Math.floor(Math.random() * choices.length)];
                let result;
                let reward = 0;

                if (userChoice === botChoice) {
                        result = getLang("tie");
                        reward = 10;
                } else if (
                        (userChoice === "rock" && botChoice === "scissors") ||
                        (userChoice === "paper" && botChoice === "rock") ||
                        (userChoice === "scissors" && botChoice === "paper")
                ) {
                        result = getLang("win");
                        reward = 25;
                } else {
                        result = getLang("lose");
                        reward = 5;
                }

                // Add coin & EXP reward
                let userData = await usersData.get(event.senderID, "data.economy");
                if (!userData) userData = { wallet: 0, bank: 0, level: 1, exp: 0 };

                let expGain = reward > 20 ? 10 : reward > 10 ? 5 : 2;
                userData.wallet = (userData.wallet || 0) + reward;
                userData.exp = (userData.exp || 0) + expGain;

                const expNeeded = 100 + (userData.level - 1) * 50;
                if (userData.exp >= expNeeded) {
                        userData.level = (userData.level || 1) + 1;
                        userData.exp = 0;
                }

                await usersData.set(event.senderID, userData, "data.economy");

                const levelUp = userData.exp === 0 && reward > 20 ? "\n\nüéâ **LEVEL UP!** You are now level " + userData.level + "!" : "";
                return message.reply(getLang("result", userChoice, botChoice, result) + "\n\nüí∞ +" + reward + " coins | üí´ +" + expGain + " EXP" + levelUp);
        }
};

