scripts/cmds/send.js.content:
module.exports = {
	config: {
		name: "send",
		aliases: ["give", "transfer", "pay"],
		version: "1.0",
		author: "Replit Agent",
		countDown: 3,
		role: 0,
		description: {
			en: "Send coins to another user"
		},
		category: "economy",
		guide: {
			en: "   {pn} <@user> <amount> - Send coins to someone"
		}
	},

	langs: {
		en: {
			sent: "‚úÖ Sent %1 coins to %2!\n\nüëõ Your balance: %3 coins",
			received: "üéÅ You received %1 coins from %2!\n\nüëõ Your balance: %3 coins",
			insufficientCoins: "‚ùå You don't have enough coins!",
			invalidAmount: "‚ùå Invalid amount!",
			noUser: "‚ùå Please tag a user or provide their ID!"
		}
	},

	onStart: async function ({ message, args, event, usersData, getLang }) {
		const senderID = event.senderID;

		// Get target user
		const targetID = event.mentions?.[Object.keys(event.mentions)[0]] || args[0];
		if (!targetID) return message.reply(getLang("noUser"));

		const amount = parseInt(args[event.mentions ? 0 : 1]);
		if (!amount || amount <= 0) return message.reply(getLang("invalidAmount"));

		// Get sender data
		let senderData = await usersData.get(senderID, "data.economy");
		if (!senderData) {
			senderData = { wallet: 0, bank: 0 };
			await usersData.set(senderID, senderData, "data.economy");
		}

		if (senderData.wallet < amount) return message.reply(getLang("insufficientCoins"));

		// Get receiver data
		let receiverData = await usersData.get(targetID, "data.economy");
		if (!receiverData) {
			receiverData = { wallet: 0, bank: 0 };
		}

		// Transfer coins
		senderData.wallet -= amount;
		receiverData.wallet = (receiverData.wallet || 0) + amount;

		await usersData.set(senderID, senderData, "data.economy");
		await usersData.set(targetID, receiverData, "data.economy");

		const targetName = event.mentions?.[Object.keys(event.mentions)[0]]?.name || "User";
		message.reply(getLang("sent", amount, targetName, senderData.wallet));
	}
};

scripts/cmds/bank.js.content:
module.exports = {
	config: {
		name: "bank",
		aliases: ["savings"],
		version: "1.0",
		author: "Replit Agent",
		countDown: 3,
		role: 0,
		description: {
			en: "Bank operations - check balance, deposit, withdraw"
		},
		category: "economy",
		guide: {
			en: "   {pn} - Check bank balance\n   {pn} deposit <amount> - Deposit to bank\n   {pn} withdraw <amount> - Withdraw from bank"
		}
	},

	langs: {
		en: {
			balance: "üè¶ BANK BALANCE\n\nüí≥ Saved: %1 coins\nüëõ Wallet: %2 coins\nüíµ Total: %3 coins",
			deposit: "‚úÖ Deposited %1 coins!\n\nüëõ Wallet: %2\nüè¶ Bank: %3",
			withdraw: "‚úÖ Withdrew %1 coins!\n\nüëõ Wallet: %2\nüè¶ Bank: %3",
			insufficientWallet: "‚ùå Not enough coins in wallet!",
			insufficientBank: "‚ùå Not enough coins in bank!",
			invalidAmount: "‚ùå Invalid amount!"
		}
	},

	onStart: async function ({ message, args, getLang, event, usersData }) {
		const userID = event.senderID;

		let userData = await usersData.get(userID, "data.economy");
		if (!userData) {
			userData = { wallet: 0, bank: 0 };
			await usersData.set(userID, userData, "data.economy");
		}

		const action = args[0]?.toLowerCase();

		if (!action || action === "balance") {
			const wallet = userData.wallet || 0;
			const bank = userData.bank || 0;
			const total = wallet + bank;
			return message.reply(getLang("balance", bank, wallet, total));
		}

		if (action === "deposit") {
			const amount = parseInt(args[1]);
			if (!amount || amount <= 0) return message.reply(getLang("invalidAmount"));
			if (userData.wallet < amount) return message.reply(getLang("insufficientWallet"));

			userData.wallet -= amount;
			userData.bank = (userData.bank || 0) + amount;
			await usersData.set(userID, userData, "data.economy");

			return message.reply(getLang("deposit", amount, userData.wallet, userData.bank));
		}

		if (action === "withdraw") {
			const amount = parseInt(args[1]);
			if (!amount || amount <= 0) return message.reply(getLang("invalidAmount"));
			if ((userData.bank || 0) < amount) return message.reply(getLang("insufficientBank"));

			userData.bank -= amount;
			userData.wallet = (userData.wallet || 0) + amount;
			await usersData.set(userID, userData, "data.economy");

			return message.reply(getLang("withdraw", amount, userData.wallet, userData.bank));
		}

		return message.reply("Unknown action! Use: balance, deposit, or withdraw");
	}
};

