			// Check if replying to a message with attachments
			if (Reply && Reply.attachments && Reply.attachments.length > 0) {
				const imageAttachment = Reply.attachments.find(att => att.type === 'photo');
				if (imageAttachment) {
					imageUrl = imageAttachment.url;
				}
			}

			// If no image found in reply, check current message
			if (!imageUrl && event.attachments && event.attachments.length > 0) {
				const imageAttachment = event.attachments.find(att => att.type === 'photo');
				if (imageAttachment) {
					imageUrl = imageAttachment.url;
				}
			}

			if (!imageUrl) {
				return message.reply(getLang('noImage'));
			}

			message.reply(getLang('analyzing'));

			// Download image and convert to base64
			let imageBase64;
			try {
				const imageResponse = await axios.get(imageUrl, {
					responseType: 'arraybuffer',
					timeout: 10000
				});
				imageBase64 = Buffer.from(imageResponse.data).toString('base64');
			} catch (err) {
				console.error("Image download error:", err.message);
				return message.reply(getLang('invalidImage'));
			}

			// Determine image mime type from URL or default to jpeg
			let mimeType = 'image/jpeg';
			if (imageUrl.includes('.png')) mimeType = 'image/png';
			else if (imageUrl.includes('.webp')) mimeType = 'image/webp';
			else if (imageUrl.includes('.gif')) mimeType = 'image/gif';

			// Call Gemini API with vision
			const response = await axios({
				url: `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${apiKey}`,
				method: "POST",
				headers: {
					"Content-Type": "application/json"
				},
				data: {
					contents: [{
						parts: [
							{
								inlineData: {
									mimeType: mimeType,
									data: imageBase64
								}
							},
							{
								text: customPrompt
							}
						]
					}],
					generationConfig: {
						maxOutputTokens: 1024,
						temperature: 0.7
					}
				}
			});

			const analysisText = response.data.candidates[0].content.parts[0].text;

			// Reply with analysis (remove the analyzing message first)
			return message.reply(analysisText);
		}
		catch (err) {
			console.error("Analysis Error:", err.response?.data || err.message);
			const errorMessage = err.response?.data?.error?.message || err.message || "Unknown error";
			return message.reply(getLang('error', errorMessage));
		}
		finally {
			delete analyzeUsing[event.senderID];
		}
	},

	onReply: async function ({ Reply, message, event, args, getLang, commandName }) {
		// Not needed for this command
	}
};

scripts/cmds/wordchain.js.content:
module.exports = {
        config: {
                name: "wordchain",
                aliases: ["wordassoc", "wa"],
                version: "1.0",
                author: "Replit Agent",
                countDown: 3,
                role: 0,
                description: {
                        en: "Word chain association game"
                },
                category: "games",
                guide: {
                        en: "   {pn} - Start word chain\n   {pn} <word> - Add a related word"
                }
        },

        langs: {
                en: {
                        started: "üéÆ WORD CHAIN STARTED!\n\nI start with: %1\n\nPlayers add related words!\nReply: *wordchain <word>",
                        chain: "Chain: %1",
                        added: "‚úÖ %1 added!",
                        invalid: "‚ùå Word must be connected to the chain!",
                        noGame: "No active game! Use *wordchain to start",
                        timeout: "‚è∞ Game ended! Final chain:\n%1"
                }
        },

        onStart: async function ({ message, getLang, event, commandName }) {
                const startWords = [
                        "apple", "ocean", "music", "adventure", "dream",
                        "technology", "nature", "fantasy", "mystery", "journey"
                ];

                const word = startWords[Math.floor(Math.random() * startWords.length)];

                if (!global.temp.wordchainGames) global.temp.wordchainGames = {};

                global.temp.wordchainGames[event.senderID] = {
                        chain: [word],
                        lastWord: word,
                        players: new Set()
                };

                message.reply(getLang("started", word) + "\n" + getLang("chain", word), (err, info) => {
                        if (!err) {
                                global.GoatBot.onReply.set(info.messageID, {
                                        commandName,
                                        author: event.senderID,
                                        messageID: info.messageID
                                });
                        }
                });
        },

        onReply: async function ({ message, event, args, getLang, usersData }) {
                const game = global.temp.wordchainGames?.[event.senderID];
                if (!game) return message.reply(getLang("noGame"));

                const newWord = args[0]?.toLowerCase();
                if (!newWord) return message.reply("Enter a word!");

                // Check if word is related to previous word (simple check - first/last letter match or shares letters)
                const lastWord = game.lastWord;
                const isRelated = 
                        lastWord[lastWord.length - 1] === newWord[0] || // Ends with same letter that starts new word
                        lastWord.split("").some(l => newWord.includes(l)); // Shares letters

                if (!isRelated) {
                        return message.reply(getLang("invalid"));
                }

                // Add coin reward
                const reward = 10;
                let userData = await usersData.get(event.senderID, "data.economy");
                if (!userData) userData = { wallet: 0, bank: 0 };
                userData.wallet = (userData.wallet || 0) + reward;
                await usersData.set(event.senderID, userData, "data.economy");

                game.chain.push(newWord);
                game.lastWord = newWord;
                game.players.add(event.senderID);

                message.reply(
                        getLang("added", newWord) + "\n" +
                        getLang("chain", game.chain.join(" ‚Üí ")) + "\n\nüí∞ +" + reward + " coins!",
                        (err, info) => {
                                if (!err) {
                                        global.GoatBot.onReply.set(info.messageID, {
                                                commandName: "wordchain",
                                                author: event.senderID,
                                                messageID: info.messageID
                                        });
                                }
                        }
                );
        }
};

scripts/cmds/20questions.js.content:
module.exports = {
        config: {
                name: "20questions",
                aliases: ["20q"],
                version: "1.0",
                author: "Replit Agent",
                countDown: 5,
                role: 0,
                description: {
                        en: "20 Questions guessing game"
                },
                category: "games",
                guide: {
                        en: "   {pn} - Start 20 questions\n   {pn} guess <answer> - Make a guess"
                }
        },

        langs: {
                en: {
                        started: "üéÆ 20 QUESTIONS STARTED!\n\nI'm thinking of something. You have 20 yes/no questions.\nAsk questions in the chat and I'll answer!\n\nTo guess: *20questions guess <your answer>",
                        thinking: "I'm thinking... ü§î",
                        questions_left: "‚ùì Questions left: %1",
                        yes: "Yes! üëç",
                        no: "No! üëé",
                        correct: "üéâ YOU WIN! It was: %1",
                        wrong: "‚ùå WRONG! It was: %1",
                        noGame: "No active game! Use *20questions to start",
                        out_of_questions: "üíÄ OUT OF QUESTIONS! It was: %1"
                }
        },

        onStart: async function ({ message, getLang, event, commandName }) {
                const answers = [
                        "elephant", "smartphone", "pizza", "mountain", "ocean",
                        "airplane", "diamond", "telescope", "volcano", "robot",
                        "lighthouse", "spaceship", "library", "waterfall", "castle"
                ];

                const answer = answers[Math.floor(Math.random() * answers.length)];

                if (!global.temp.twentyQGames) global.temp.twentyQGames = {};

                global.temp.twentyQGames[event.senderID] = {
                        answer: answer,
                        questions: 0,
                        maxQuestions: 20
                };

                message.reply(getLang("started"), (err, info) => {
                        if (!err) {
                                global.GoatBot.onReply.set(info.messageID, {
                                        commandName,
                                        author: event.senderID,
                                        messageID: info.messageID
                                });
                        }
                });
        },

        onReply: async function ({ message, event, args, getLang, usersData }) {
                const game = global.temp.twentyQGames?.[event.senderID];
                if (!game) return message.reply(getLang("noGame"));

                if (args[0]?.toLowerCase() === "guess") {
                        const guess = args.slice(1).join(" ").toLowerCase();
                        if (!guess) return message.reply("What's your guess?");

                        delete global.temp.twentyQGames[event.senderID];
                        if (guess === game.answer.toLowerCase()) {
                                // Add coin reward for correct guess
                                const reward = 75;
                                let userData = await usersData.get(event.senderID, "data.economy");
                                if (!userData) userData = { wallet: 0, bank: 0 };
                                userData.wallet = (userData.wallet || 0) + reward;
                                await usersData.set(event.senderID, userData, "data.economy");

                                return message.reply(getLang("correct", game.answer) + "\n\nüí∞ +" + reward + " coins!");
                        } else {
                                return message.reply(getLang("wrong", game.answer));
                        }
                }

                // It's a yes/no question
                game.questions++;

                if (game.questions >= game.maxQuestions) {
                        delete global.temp.twentyQGames[event.senderID];
                        return message.reply(getLang("out_of_questions", game.answer));
                }

                // Simple random yes/no based on keywords
                const question = event.body?.toLowerCase() || "";
                const keywords = game.answer.split("");
                const isRelated = keywords.some(k => question.includes(k));

                const response = isRelated ? getLang("yes") : getLang("no");
                const remaining = game.maxQuestions - game.questions;

                message.reply(response + "\n" + getLang("questions_left", remaining), (err, info) => {
                        if (!err) {
                                global.GoatBot.onReply.set(info.messageID, {
                                        commandName: "20questions",
                                        author: event.senderID,
                                        messageID: info.messageID
                                });
                        }
                });
        }
};
